INSERT INTO `banner_lang` (`id`, `banner_id`, `lang_id`, `name`, `description`, `button_text`) VALUES
(1, 1, 1, 'Vật Lý - Vật Lý Kỹ Thuật', 'Là một trong những khoa ra đời từ những ngày Trường Cao đẳng Khoa học (Viện đại học Đông Dương) – Đại học Khoa học Sài Gòn được thành lập năm 1940- 1941. Khoa đã đạo tạo được rất nhiều nhân tài cho đất nước trên nhiều lĩnh vực.', 'Chi tiết'),
(2, 1, 2, 'Faculty of physics and engineering physics', 'It is one of the departments born from the days of College of Science (Indochina University Institute) - Saigon Science University, founded in 1940-1941. The faculty has trained many talents for the country many areas.', 'Detail'),
(3, 2, 1, 'Cơ hội nghề nghiệp sau khi ra trường', 'Sinh viên có cơ hội làm việc tại các công ty trong và ngoài nước, viện nghiên cứu, sở khoa học công nghệ liên quan tới lĩnh vực Vật lý, kỹ thuật và khoa học. Cơ hội được học tiếp chương trình sau đại học (thạc sĩ, tiến sĩ) tại các trường đại học danh tiếng trong và ngoài nước.', 'Chi tiết'),
(4, 2, 2, 'Career opportunities after graduation', 'Students have the opportunity to work at companies at home and abroad, research institutes, departments of science and technology related to the fields of Physics, Engineering and Science. Opportunities to continue graduate programs (masters, doctoral)', 'Detail'),
(5, 3, 1, 'Sinh Viên', 'Năng động, sáng tạo, tự tin, bản lĩnh, làm chủ kiến thức rộng bước tương lai.', 'Chi tiết'),
(6, 3, 2, 'Students', 'Dynamic, creative, confident, brave, mastering broad knowledge of the future.', 'Detail'),
(7, 4, 1, 'Vật Lý - Vật Lý Kỹ Thuật', 'Là một trong những khoa ra đời từ những ngày Trường Cao đẳng Khoa học (Viện đại học Đông Dương) – Đại học Khoa học Sài Gòn được thành lập năm 1940- 1941. Khoa đã đạo tạo được rất nhiều nhân tài cho đất nước trên nhiều lĩnh vực.', 'Chi tiết'),
(8, 4, 2, 'Physics - Technical Physics', 'It is one of the departments born from the days of College of Science (Indochina University Institute) - Saigon Science University, founded in 1940-1941. The faculty has trained many talents for the country. many areas.', 'Detail'),
(9, 5, 1, 'Cơ hội nghề nghiệp sau khi ra trường', 'Sinh viên có cơ hội làm việc tại các công ty trong và ngoài nước, viện nghiên cứu, sở khoa học công nghệ liên quan tới lĩnh vực Vật lý, kỹ thuật và khoa học. Cơ hội được học tiếp chương trình sau đại học (thạc sĩ, tiến sĩ) tại các trường đại học danh tiếng trong và ngoài nước.', 'Chi tiết'),
(10, 5, 2, 'Career opportunities after graduation', 'Students have the opportunity to work at companies at home and abroad, research institutes, departments of science and technology related to the fields of Physics, Engineering and Science,...', 'Detail'),
(11, 6, 1, 'Sinh Viên', 'Năng động, sang tạo, tự tin, bản lĩnh, làm chủ kiến thức rộng bước tương lai.', 'Chi tiết'),
(12, 6, 2, 'Students', 'Dynamic, creative, confident, brave, mastering broad knowledge of the future.', 'Detail');
