INSERT INTO `home_lang` (`id`, `lang_id`, `home_id`, `specialized_title`, `staff_title`, `staff_des`, `partner_title`, `partner_des`, `contact_title`, `contact_des`) VALUES
(1, 1, 1, 'Bộ môn', 'Lãnh đạo', 'Đội ngũ cán bộ nhiệt quyết có kinh nghiệm giảng dạy trong và ngoài nước. Phương châm giúp sinh viên nắm chắc kiến thức và ứng dụng kiến thức vào thực tiến công việc', 'Liên kết', 'Cán bộ Khoa Vật lý từ lâu đã tham gia hợp tác giảng dạy nghiên cứu ở nhiềunơi. Các giảng viên tham gia thỉnh giảng trong và ngoài nước: ĐH Sư phạm TPHCM, ĐH Sài Gòn, ĐH Tôn Đức Thắng, ĐH Cần Thơ, ĐH Đà Lạt. Ngược lại các cán bộ từ các trường khác tới Khoa', 'Liên hệ', ''),
(2, 2, 1, 'Deparment', 'Faculty Members', 'A team of enthusiastic staff having experience teaching at home and abroad. The motto helps students master the knowledge and apply it to the real work.', 'Connection', 'Staff of the Faculty of Physics have long been involved in collaborative teaching research in many places. Lecturers participating in the lecture at home and abroad: Ho Chi Minh City University of Pedagogy, Saigon University, ....', 'Contact', ''),
(3, 1, 2, 'Hướng nghiên cứu', 'Lãnh đạo', 'Đội ngũ cán bộ nhiệt quyết có kinh nghiệm giảng dạy trong và ngoài nước. Phương châm giúp sinh viên nắm chắc kiến thức và ứng dụng kiến thưcd vào thực tiến công việc', 'Liên kết', 'Cán bộ Khoa Vật lý từ lâu đã tham gia hợp tác giảng dạy nghiên cứu ở nhiều nơi. Các giảng viên tham gia thỉnh giảng trong và ngoài nước: ĐH Sư phạm TPHCM, ĐH Sài Gòn, ĐH Tôn Đức Thắng, ĐH Cần Thơ, ĐH Đà Lạt', 'Liên hệ', ''),
(4, 2, 2, 'Research Theme', 'Faculty Members', 'A team of enthusiastic staff having experience teaching at home and abroad. The motto helps students master the knowledge and application of knowledge in the real work', 'connective', 'Staff of the Faculty of Physics have long been involved in collaborative teaching research in many places. Lecturers participating in the lecture at home and abroad: Ho Chi Minh City University of Pedagogy, Saigon University, Ton Duc Thang University,...', 'contact', '');
