INSERT INTO `pages_lang` (`id`, `page_id`, `lang_id`, `title`, `content`, `excerpt`) VALUES
(37, 19, 1, 'Đào Tạo', '<p><span style=\"font-size:24px\"><span style=\"color:#ff0000\"><strong>Đ&agrave;o tạo Đại học</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><span style=\"font-size:14px\">Mục ti&ecirc;u của ng&agrave;nh&nbsp;Vật l&yacute; Tin học&nbsp;nhằm đ&agrave;o tạo sinh vi&ecirc;n c&oacute; kiến thức cơ bản về Vật L&yacute; v&agrave; c&oacute; kỹ năng cần thiết về Khoa học m&aacute;y t&iacute;nh để c&oacute; thể l&agrave;m việc hiệu quả trong c&aacute;c cơ quan nghi&ecirc;n cứu, c&aacute;c c&ocirc;ng ty lập tr&igrave;nh web, PHP, android, java, C/C++, ... c&aacute;c c&ocirc;ng ty lập tr&igrave;nh nh&uacute;ng, điều khiển tự động, thiết kế vi mạch, IoTs, quản trị mạng, lập tr&igrave;nh tr&ecirc;n điện thoại di động, chẩn đo&aacute;n h&igrave;nh ảnh y khoa, kỹ thuật y sinh. Một số c&ocirc;ng ty c&oacute; sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh Vật l&yacute; Tin đang l&agrave;m ciệc như: Renesas, Mitsuba, Bosch, TMA, AMCC, E-Silicon, FPT, HPT, VinFast ...</span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><strong>CHƯƠNG TR&Igrave;NH Đ&Agrave;O TẠO ĐẠI HỌC</strong></span></span></p>\r\n\r\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><strong>Chuy&ecirc;n ng&agrave;nh Vật l&yacute; Tin học</strong></span></span></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\">\r\n			<p><span style=\"color:#000000\"><strong>STT</strong></span></p>\r\n			</td>\r\n			<td rowspan=\"2\">\r\n			<p><span style=\"color:#000000\"><strong>M&ocirc;n học</strong></span></p>\r\n			</td>\r\n			<td rowspan=\"2\"><span style=\"color:#000000\"><strong>Loại học phần</strong></span></td>\r\n			<td colspan=\"2\">\r\n			<p><span style=\"color:#000000\"><strong>Số t&iacute;n chỉ</strong></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"color:#0000