INSERT INTO `posts_lang` (`id`, `post_id`, `lang_id`, `title`, `content`, `excerpt`) VALUES
(1, 1, 1, 'A', '<p>E</p>\r\n', 'B'),
(2, 1, 2, 'C', '<p>F</p>\r\n', 'D'),
(3, 2, 1, 'DDD', '<p>BBBBBBBBBBBBB</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-160px; position:absolute; top:-6px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>\r\n', 'VVVVVVVV'),
(4, 2, 2, 'AAAAAAAAAAAAAAAAA', '<p>RRRRRRRRRRRRRRRRRRR</p>\r\n', 'VVVVVVVVVVVV'),
(5, 3, 1, 'THÔNG TIN TUYỂN DỤNG TỪ TRUNG TÂM NGHIÊN CỨU VẬT LIỆU CẤU TRÚC NANO VÀ PHÂN TỬ (INOMAR)', '<p>Junior Application Engineer</p>\r\n\r\n<p>Job description</p>\r\n\r\n<p>Working as a bridge between customers and engineering teams and using customer input and sales information, design or re-design, develop, test and implement &nbsp;applications using optical analytical instruments and systems. &nbsp;Provide technical support and expertise to customers, testing applications, responding to customer feedback, installations, maintenance and often performing sales presentations and demonstrations.</p>\r\n\r\n<p>Required Qualifications</p>\r\n\r\n<ul>\r\n	<li>Minimum education: bachelor&rsquo;s degree in science (Physics, Chemistry, or Bio Science), master&rsquo;s degree is plus.</li>\r\n	<li>Willingness to work in a small multi-disciplinary team.</li>\r\n	<li>Ability to work with clients or other engineers to trouble shoot issues.</li>\r\n	<li>Diagnose application failures in staging and production environments, understand the underlying issues, and resolve them.</li>\r\n</ul>\r\n\r\n<p>Desired Qualifications</p>\r\n\r\n<ul>\r\n	<li>Experience in software programing is plus.</li>\r\n	<li>Maintain knowledge of build tools and best practices.</li>\r\n	<li>Strong attention to detail and quality.</li>\r\n</ul>\r\n\r\n<p>Other information</p>\r\n\r\n<ul>\r\n	<li>Start date: now.</li>\r\n	<li>Location: INOMAR center (Vietnam National University HoChi Minh in Thuduc district) or Vinhomes Golden River.</li>\r\n	<li>Salary and equity will be determined based on qualifications ($600 or higher per month).</li>\r\n</ul>\r\n\r\n<p>Contact person</p>\r\n\r\n<ul>\r\n	<li>Mr. Phan Bach Thang ( &nbsp;<a href=\"mailto:pbthang@inomar.edu.vn\">pbthang@inomar.edu.vn</a>, 0797907700).</li>\r\n</ul>\r\n', 'Junior Application Engineer'),
(6, 3, 2, 'RECRUITMENT INFORMATION FROM THE NANO STRUCTURE AND MATERIAL RESEARCH CENTER (INOMAR)', '<p>Junior Application Engineer</p>\r\n\r\n<p>Job description</p>\r\n\r\n<p>Working as a bridge between customers and engineering teams and using customer input and sales information, design or re-design, develop, test and implement &nbsp;applications using optical analytical instruments and systems. &nbsp;Provide technical support and expertise to customers, testing applications, responding to customer feedback, installations, maintenance and often performing sales presentations and demonstrations.</p>\r\n\r\n<p>Required Qualifications</p>\r\n\r\n<ul>\r\n	<li>Minimum education: bachelor&rsquo;s degree in science (Physics, Chemistry, or Bio Science), master&rsquo;s degree is plus.</li>\r\n	<li>Willingness to work in a small multi-disciplinary team.</li>\r\n	<li>Ability to work with clients or other engineers to trouble shoot issues.</li>\r\n	<li>Diagnose application failures in staging and production environments, understand the underlying issues, and resolve them.</li>\r\n</ul>\r\n\r\n<p>Desired Qualifications</p>\r\n\r\n<ul>\r\n	<li>Experience in software programing is plus.</li>\r\n	<li>Maintain knowledge of build tools and best practices.</li>\r\n	<li>Strong attention to detail and quality.</li>\r\n</ul>\r\n\r\n<p>Other information</p>\r\n\r\n<ul>\r\n	<li>Start date: now.</li>\r\n	<li>Location: INOMAR center (Vietnam National University HoChi Minh in Thuduc district) or Vinhomes Golden River.</li>\r\n	<li>Salary and equity will be determined based on qualifications ($600 or higher per month).</li>\r\n</ul>\r\n\r\n<p>Contact person</p>\r\n\r\n<ul>\r\n	<li>Mr. Phan Bach Thang ( &nbsp;<a href=\"mailto:pbthang@inomar.edu.vn\">pbthang@inomar.edu.vn</a>, 0797907700).</li>\r\n</ul>\r\n', 'Junior Application Engineer'),
(7, 4, 1, 'THÔNG TIN TUYỂN DỤNG TỪ CÔNG TY ONSKY VIETNAM', '<p>C&ocirc;ng ty TNHH OnSky Việt Nam - chuy&ecirc;n sản xuất, cung cấp c&aacute;c giải ph&aacute;p v&agrave; sản phẩm nh&agrave; th&ocirc;ng minh - đang trong giai đoạn chuyển giao c&ocirc;ng nghệ từ Mỹ về Việt Nam n&ecirc;n c&ocirc;ng ty đang c&oacute; nhu cầu tuyển dụng nh&acirc;n sự cho 2 vị tr&iacute;: Android developer v&agrave; Cloud Developer. Sinh vi&ecirc;n mới ra trường chỉ cần c&oacute; kiến thức nền về IT, lập tr&igrave;nh... Mức lương cho từng vị tr&iacute;:<br />\r\n- Cloud Application l&agrave; từ 500$ - 1000$<br />\r\n- Android Developer l&agrave; từ 700$ - 2000$<br />\r\nTh&ocirc;ng tin chi tiết xem th&ecirc;m trong file:<br />\r\n- Cloud Application:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/14C6HLOnB8ySP0K93VUQoj79tF15Q6MGS/view?usp=sharing\">https://drive.google.com/file/d/14C6HLOnB8ySP0K93VUQoj79tF15Q6MGS/view?usp=sharing</a></p>\r\n\r\n<p>- Android Developer:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/1zyFZrs_V0nJTLv4l9L8-lhFCSotc4lyS/view?usp=sharing\">https://drive.google.com/file/d/1zyFZrs_V0nJTLv4l9L8-lhFCSotc4lyS/view?usp=sharing</a></p>\r\n', 'Công ty TNHH OnSky Việt Nam - chuyên sản xuất...'),
(8, 4, 2, 'RECRUITMENT INFORMATION FROM ONSKY VIETNAM COMPANY', '<p>OnSky Vietnam Co., Ltd - specializing in manufacturing, providing smart home solutions and products - is in the process of transferring technology from the US to Vietnam, so the company is in need of recruiting personnel for the two positions. location: Android developer and Cloud Developer. New graduates just need to have background knowledge about IT, programming ... Salary for each position:<br />\r\n- Cloud Application is from 500 $ - 1000 $<br />\r\n- Android Developer is from $ 700 - $ 2000<br />\r\nMore details in the file:</p>\r\n\r\n<p>- Cloud Application:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/14C6HLOnB8ySP0K93VUQoj79tF15Q6MGS/view?usp=sharing\">https://drive.google.com/file/d/14C6HLOnB8ySP0K93VUQoj79tF15Q6MGS/view?usp=sharing</a></p>\r\n\r\n<p>- Android Developer:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/1zyFZrs_V0nJTLv4l9L8-lhFCSotc4lyS/view?usp=sharing\">https://drive.google.com/file/d/1zyFZrs_V0nJTLv4l9L8-lhFCSotc4lyS/view?usp=sharing</a></p>\r\n', 'OnSky Vietnam Co., Ltd - specializing in manufacturing...'),
