(143, 83, 1, 'Thông báo Lễ Tốt nghiệp Khóa 2010 đợt tháng 09-2014', '<p>Khoa Vật l&yacute; - VLKT th&ocirc;ng b&aacute;o đến c&aacute;c T&acirc;n cử nh&acirc;n Kh&oacute;a 2010 tốt nghiệp đợt th&aacute;ng 09-2014 về thời gian tổ chức lễ tốt nghiệp của Khoa như sau:</p>\r\n\r\n<p>- Ng&agrave;y tổ chức: 08 giờ 00 ng&agrave;y 02-11-2014, xem chi tiết trong file&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Tot%20nghiep/Thong_bao_le_tot_nghiep_Thang_9-2014.pdf\">đ&iacute;nh k&egrave;m</a></p>\r\n\r\n<p>- SV đăng k&yacute; thu&ecirc; lễ phục tại VP Khoa trước ng&agrave;y 17-10-2014. SV c&oacute; nhu cầu mời phụ huynh đến dự lễ th&igrave; nhận thư mời khi đăng k&yacute; thu&ecirc; lễ phục.</p>\r\n\r\n<p>Để thuận ti&ecirc;n cho c&aacute;c SV đ&atilde; đi l&agrave;m kh&ocirc;ng thể đăng k&yacute; thu&ecirc; lễ phục trong giờ l&agrave;m việc được, Thầy Nhẫn sẽ trực tại Văn ph&ograve;ng khoa trong ng&agrave;y Thứ Bảy 11/10/2014 để cho SV đăng k&yacute;. Vậy c&aacute;c SV khẩn trương đăng k&yacute; sớm. SV c&oacute; thể gọi điện hẹn trước theo Số ĐT: 0919 786 248.</p>\r\n\r\n<p>BCN Khoa Vật l&yacute; - VLKT</p>\r\n\r\n<p>Ng&agrave;y 29-09-2014</p>\r\n', 'Thông báo Lễ Tốt nghiệp Khóa 2010 đợt tháng 09-2014\r\n'),
(144, 83, 2, 'Thông báo Lễ Tốt nghiệp Khóa 2010 đợt tháng 09-2014', '<p>Khoa Vật l&yacute; - VLKT th&ocirc;ng b&aacute;o đến c&aacute;c T&acirc;n cử nh&acirc;n Kh&oacute;a 2010 tốt nghiệp đợt th&aacute;ng 09-2014 về thời gian tổ chức lễ tốt nghiệp của Khoa như sau:</p>\r\n\r\n<p>- Ng&agrave;y tổ chức: 08 giờ 00 ng&agrave;y 02-11-2014, xem chi tiết trong file&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Tot%20nghiep/Thong_bao_le_tot_nghiep_Thang_9-2014.pdf\">đ&iacute;nh k&egrave;m</a></p>\r\n\r\n<p>- SV đăng k&yacute; thu&ecirc; lễ phục tại VP Khoa trước ng&agrave;y 17-10-2014. SV c&oacute; nhu cầu mời phụ huynh đến dự lễ th&igrave; nhận thư mời khi đăng k&yacute; thu&ecirc; lễ phục.</p>\r\n\r\n<p>Để thuận ti&ecirc;n cho c&aacute;c SV đ&atilde; đi l&agrave;m kh&ocirc;ng thể đăng k&yacute; thu&ecirc; lễ phục trong giờ l&agrave;m việc được, Thầy Nhẫn sẽ trực tại Văn ph&ograve;ng khoa trong ng&agrave;y Thứ Bảy 11/10/2014 để cho SV đăng k&yacute;. Vậy c&aacute;c SV khẩn trương đăng k&yacute; sớm. SV c&oacute; thể gọi điện hẹn trước theo Số ĐT: 0919 786 248.</p>\r\n\r\n<p>BCN Khoa Vật l&yacute; - VLKT</p>\r\n\r\n<p>Ng&agrave;y 29-09-2014</p>\r\n', 'Thông báo Lễ Tốt nghiệp Khóa 2010 đợt tháng 09-2014\r\n'),
(145, 84, 1, 'Thông báo lịch học môn Vật lý tính toán', '<p>Sinh vi&ecirc;n Bộ m&ocirc;n Vật l&yacute; tin học đăng k&yacute; học lại m&ocirc;n Vật l&yacute; t&iacute;nh to&aacute;n sẽ học v&agrave;o l&uacute;c 08 giờ 00 Thứ Năm tại ph&ograve;ng E304 từ Tuần 20-10-2014. SV ch&uacute; &yacute; đi học đầy đủ.</p>\r\n\r\n<p>Ng&agrave;y 13-10-2014</p>\r\n\r\n<p>GV Khoa Vật l&yacute; - VLKT</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Thông báo lịch học môn Vật lý tính toán\r\n'),
(146, 84, 2, 'Announcement schedule of Computational Physics', '<p>Department of Computational Physics students re-enroll in Computational Physics will study at 8:00 AM on Thursday at room E304 from Week 20-10-2014. Students pay full attention to school.</p>\r\n\r\n<p>October 13, 2014</p>\r\n\r\n<p>Faculty of Physics - VLKT</p>\r\n', 'Announcement schedule of Computational Physics'),
(147, 85, 1, 'Thông báo lịch học Anh văn 3 và thời khóa biểu khóa 2014', '<p>Sinh vi&ecirc;n kh&oacute;a 2013 sẽ bắt đầu học m&ocirc;n Anh văn 3 từ ng&agrave;y 02/03/2015. Sinh vi&ecirc;n xem chi tiết th&ocirc;ng b&aacute;o tr&ecirc;n trang web của Ph&ograve;ng đ&agrave;o tạo hoặc&nbsp;<a href=\"http://www.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=8410&amp;Itemid=293\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>Sinh vi&ecirc;n xem thời kh&oacute;a biểu của kh&oacute;a 2014 tr&ecirc;n trang web của Ph&ograve;ng đ&agrave;o tạo hoặc&nbsp;<a href=\"http://www.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=8406&amp;Itemid=293\">tại đ&acirc;y</a>.</p>\r\n\r\n<p><a href=\"http://www.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=8369&amp;Itemid=293\">Th&ocirc;ng b&aacute;o về việc học Anh văn của kh&oacute;a 2014 , Đại học ch&iacute;nh quy</a></p>\r\n\r\n<p>GV khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo lịch học Anh văn 3 và thời khóa biểu khóa 2014\r\n'),
(148, 85, 2, 'Announcement of English class 3 schedule and 2014 class schedule', '<p>Students of class 2013 will start studying English 3 from March 2, 2015. Students see the notice details on the Training Room website or here.</p>\r\n\r\n<p>Students can view the 2014 class schedule on the Training Room website or here.</p>\r\n\r\n<p>Notice of English learning for the 2014 course, Regular University</p>\r\n\r\n<p>Faculty of Physics - VLKT</p>\r\n', 'Announcement of English class 3 schedule and 2014 class schedule');
INSERT INTO `posts_lang` (`id`, `post_id`, `lang_id`, `title`, `content`, `excerpt`) VALUES
(149, 86, 1, 'Thông báo lịch học môn Pháp luật đại cương', '<p>Theo th&ocirc;ng b&aacute;o của Ph&ograve;ng Đ&agrave;o tạo, m&ocirc;n Ph&aacute;p luật đại cương l&agrave; m&ocirc;n bắt buộc đối với tất cả sinh vi&ecirc;n từ kh&oacute;a 2013. Theo ph&acirc;n bố m&ocirc;n học th&igrave; sinh vi&ecirc;n kh&oacute;a 2013 Khoa Vật l&yacute; v&agrave; Ng&agrave;nh Kỹ thuật hạt nh&acirc;n phải học m&ocirc;n n&agrave;y trong giai đoạn đại cương (1,5 năm đầu) nhưng do kh&ocirc;ng bố tr&iacute; được lịch học trong học kỳ I năm học 2014-2015 n&ecirc;n PĐT xếp lịch học v&agrave;o học kỳ II n&agrave;y, sinh vi&ecirc;n xem lịch học cụ thể&nbsp;<a href=\"http://www.hcmus.edu.vn/images/stories/phong_dao_tao/14-15/hk1/tkb_pldc.pdf\">tại đ&acirc;y</a>. Do khoa nhận được lịch học m&ocirc;n n&agrave;y trễ n&ecirc;n đ&atilde; xếp lịch học c&aacute;c m&ocirc;n kh&aacute;c v&agrave;o giờ học của m&ocirc;n Ph&aacute;p luật đại cương (s&aacute;ng thứ 3 v&agrave; chiều thứ 4) v&agrave; sinh vi&ecirc;n đ&atilde; đăng k&yacute; học phần trước khi c&oacute; lịch học m&ocirc;n n&agrave;y. V&igrave; vậy c&oacute; rất nhiều sinh vi&ecirc;n bị tr&ugrave;ng giờ kh&ocirc;ng thể học m&ocirc;n Ph&aacute;p luật đại cương được. Để giải quyết vấn đề, Khoa Vật l&yacute; - VLKT đ&atilde; đề nghị Ph&ograve;ng Đ&agrave;o tạo dời lịch học m&ocirc;n Ph&aacute;p luật đại cương đối với SV Khoa Vật l&yacute; - VLKT v&agrave; Ng&agrave;nh Kỹ thuật hạt nh&acirc;n sang học kỳ II năm học 2015-2016 (năm thứ 3). Do vậy c&aacute;c lớp học Ph&aacute;p luật đại cương của Khoa Vật l&yacute; - VLKT sẽ bị hủy, sinh vi&ecirc;n sẽ d&agrave;nh thời gian để học c&aacute;c m&ocirc;n cơ sở của khoa như đ&atilde; đăng k&yacute;. Tuy nhi&ecirc;n, một số sinh vi&ecirc;n đăng k&yacute; số t&iacute;n chỉ &iacute;t muốn đăng k&yacute; học m&ocirc;n Ph&aacute;p luật đại cương trong học kỳ II n&agrave;y c&oacute; thể chọn đăng k&yacute; trong thời gian đăng k&yacute; trả nợ (sẽ c&oacute; th&ocirc;ng b&aacute;o v&agrave;o đầu th&aacute;ng 3) để học c&ugrave;ng với c&aacute;c lớp trả nợ của kh&oacute;a 2014.</p>\r\n\r\n<p>Nếu SV c&oacute; thắc mắc về m&ocirc;n học n&agrave;y c&oacute; thể li&ecirc;n hệ Gi&aacute;o vụ khoa (Thầy Nhẫn) để được giải đ&aacute;p, ĐT: 0919786248, Email:&nbsp;<a href=\"mailto:htnhan@hcmus.edu.vn\">htnhan@hcmus.edu.vn</a>.</p>\r\n\r\n<p>Tp. HCM ng&agrave;y 09 th&aacute;ng 02 năm 2015</p>\r\n\r\n<p>Gi&aacute;o vụ Khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo lịch học môn Pháp luật đại cương\r\n'),
(150, 86, 2, 'Notice of general law subjects schedule', '<p>According to the notice of the Training Department, the General Law subject is compulsory for all students from the 2013 class. According to the course distribution, students of the 2013 course in the Department of Physics and Nuclear Engineering must study this subject. During the general stage (the first 1.5 years), but due to the failure of arranging a schedule in the first semester of the 2014-2015 school year, the PĐT arranges the schedule for this second term, students see the specific schedule here. Because the department received the schedule of this subject late, it was scheduled for other subjects at the time of the General Law class (Tuesday morning and Wednesday afternoon) and students had to register for the module before the schedule of this subject. . Therefore, many students who have the same time cannot take the general law classes. To solve the problem, the Faculty of Physics - VLKT has asked the Training Department to reschedule the General Law course for students of the Faculty of Physics - VLKT and Nuclear Engineering to the second semester of the academic year 2015-2016 (year 3rd). Therefore, the General Law classes of the Faculty of Physics - VLKT will be canceled, students will take time to study the basic subjects of the faculty as registered. However, some students applying for a small number of credits who want to enroll in the General Law course in the second semester may choose to register during the loan repayment period (which will be announced in early March) to study with the 2014 repayment classes.</p>\r\n\r\n<p>If students have questions about this subject, you can contact the Department of Education (Thay Nhan) for answers, Tel: 0919786248, Email: htnhan@hcmus.edu.vn.</p>\r\n\r\n<p>Tp. HCM, February 9, 2015</p>\r\n\r\n<p>Department of Physics - VLKT<br />\r\n&nbsp;</p>\r\n', 'Notice of general law subjects schedule\r\n'),
(151, 87, 1, 'Danh sách sinh viên đăng ký học phần không thành công', '<p>Sinh vi&ecirc;n xem Danh s&aacute;ch sinh vi&ecirc;n đăng k&yacute; học phần kh&ocirc;ng th&agrave;nh c&ocirc;ng tr&ecirc;n&nbsp;<a href=\"http://www.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=8612&amp;Itemid=293\">trang web của Ph&ograve;ng Đ&agrave;o tạo</a>.</p>\r\n', 'Danh sách sinh viên đăng ký học phần không thành công\r\n'),
(152, 87, 2, 'List of students registering for the module failed', '<p>Students see the List of students who failed to register for the module on the Training Room website.</p>\r\n', 'List of students registering for the module failed\r\n'),
(153, 88, 1, 'Thông báo nộp đơn xét Tốt nghiệp Đại học chính qui', '<p>Sinh vi&ecirc;n kh&oacute;a 2011, 2010, 2009,... nộp hồ sơ x&eacute;t tốt nghiệp v&agrave;o ng&agrave;y 24 v&agrave; 25/08/2015. Sinh vi&ecirc;n xem th&ocirc;ng b&aacute;o chi tiết tr&ecirc;n trang&nbsp;<a href=\"http://www.hcmus.edu.vn/images/stories/phong_dao_tao/14-15/hk2/tb_xet_tn_dhcq_t9.15.pdf\">web trường</a>.</p>\r\n\r\n<p>Lưu &yacute;: Sinh vi&ecirc;n phải chuẩn bị trước c&aacute;c hồ sơ theo y&ecirc;u cầu v&agrave; nộp đ&uacute;ng thời gian quy định.</p>\r\n\r\n<p>Gi&aacute;o vụ khoa VL-VLKT</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Thông báo nộp đơn xét Tốt nghiệp Đại học chính qui\r\n'),
(154, 88, 2, 'Notice of application for consideration of Graduated University', '<p>Students of course 2011, 2010, 2009, etc. will submit graduation review applications on August 24 and 25, 2015. Students see the detailed notice on the school website.</p>\r\n\r\n<p>Note: Students must prepare the required documents in advance and submit them on time.</p>\r\n\r\n<p>Department of VL-VLKT</p>\r\n', 'Notice of application for consideration of Graduated University'),
(155, 89, 1, 'ẢNH LỄ TỐT NGHIỆP 2014', '<p>K&iacute;nh thưa qu&iacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n,&nbsp;</p>\r\n\r\n<p>Để gi&uacute;p qu&iacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n dễ d&agrave;ng t&igrave;m kiếm ảnh, em đ&atilde; ph&acirc;n loại như sau:</p>\r\n\r\n<p>-Ảnh thầy c&ocirc;, ảnh chụp tập thể, nh&agrave; t&agrave;i trợ, ph&aacute;t biểu của c&aacute;c thầy, ph&aacute;t biểu cảm nghĩ của sinh vi&ecirc;n, trao học bổng, văn nghệ: Qu&iacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n truy cập tại đ&acirc;y.</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p>-Ảnh ph&aacute;t bằng: ảnh chụp của c&aacute;c bạn sinh vi&ecirc;n l&uacute;c l&agrave;m lễ nhận bằng. C&aacute;c bạn sinh vi&ecirc;n truy cập tại đ&acirc;y.</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p>-Ảnh ngo&agrave;i giảng đường: ảnh chụp của c&aacute;c bạn sinh vi&ecirc;n mặc lễ phục trước khi v&agrave;o l&agrave;m lễ. C&aacute;c bạn sinh vi&ecirc;n truy cập tại đ&acirc;y.</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p>-Ảnh chụp hoạt động chuẩn bị lễ tốt nghiệp của c&aacute;c thầy c&ocirc;. Qu&iacute; thầy c&ocirc; truy cập tại đ&acirc;y.</p>\r\n', 'Kính thưa quí thầy cô và các bạn sinh viên, \r\nĐể giúp quí thầy cô và các bạn sinh viên dễ dàng tìm kiếm ảnh, em đã phân loại như sau:'),
(156, 89, 2, 'GRADUATION CEREMONY 2014', '<p>K&iacute;nh thưa qu&iacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n,&nbsp;</p>\r\n\r\n<p>Để gi&uacute;p qu&iacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n dễ d&agrave;ng t&igrave;m kiếm ảnh, em đ&atilde; ph&acirc;n loại như sau:</p>\r\n\r\n<p>-Ảnh thầy c&ocirc;, ảnh chụp tập thể, nh&agrave; t&agrave;i trợ, ph&aacute;t biểu của c&aacute;c thầy, ph&aacute;t biểu cảm nghĩ của sinh vi&ecirc;n, trao học bổng, văn nghệ: Qu&iacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n truy cập tại đ&acirc;y.</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p>-Ảnh ph&aacute;t bằng: ảnh chụp của c&aacute;c bạn sinh vi&ecirc;n l&uacute;c l&agrave;m lễ nhận bằng. C&aacute;c bạn sinh vi&ecirc;n truy cập tại đ&acirc;y.</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p>-Ảnh ngo&agrave;i giảng đường: ảnh chụp của c&aacute;c bạn sinh vi&ecirc;n mặc lễ phục trước khi v&agrave;o l&agrave;m lễ. C&aacute;c bạn sinh vi&ecirc;n truy cập tại đ&acirc;y.</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p>-Ảnh chụp hoạt động chuẩn bị lễ tốt nghiệp của c&aacute;c thầy c&ocirc;. Qu&iacute; thầy c&ocirc; truy cập tại đ&acirc;y.</p>\r\n', 'Dear teachers and students,\r\nTo help teachers and students easily find photos, I have classified the following:'),
(157, 90, 1, 'Danh sách SV bị cảnh cáo học vụ và buộc thôi học', '<p>SV xem danh s&aacute;ch bị cảnh c&aacute;o học vụ&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/DS_SV_bi_canh_cao.pdf\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>SV xem danh s&aacute;ch bị buộc th&ocirc;i học&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh_sach_SV_bi_buoc_thoi_hoc.pdf\">tại đ&acirc;y</a>.</p>\r\n', 'Danh sách SV bị cảnh cáo học vụ và buộc thôi học\r\n'),
(158, 90, 2, 'List of students whose academic warnings and suspension of education', '<p>SV xem danh s&aacute;ch bị cảnh c&aacute;o học vụ&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/DS_SV_bi_canh_cao.pdf\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>SV xem danh s&aacute;ch bị buộc th&ocirc;i học&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh_sach_SV_bi_buoc_thoi_hoc.pdf\">tại đ&acirc;y</a>.</p>\r\n', 'List of students whose academic warnings and suspension of education'),
(159, 91, 1, 'Thông báo V/v xét chọn làm khóa luận/thi tốt nghiệp đối với SV khóa 2012', '<p>Sinh vi&ecirc;n kh&oacute;a 2012 xem th&ocirc;ng b&aacute;o về việc x&eacute;t chọn l&agrave;m kh&oacute;a luận hoặc thi tốt nghiệp trong học k&yacute; II năm học 2015-2016&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/NH%202015-2016/Thong_bao_xet_khoa_luan_TN_khoa_2012.jpg\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>Tp. HCM ng&agrave;y 12 th&aacute;ng 01 năm 2016</p>\r\n\r\n<p>BCN Khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo V/v xét chọn làm khóa luận/thi tốt nghiệp đối với SV khóa 2012\r\n'),
(160, 91, 2, 'Notify V / v considered as thesis / graduation exam for students of 2012 class', '<p>Students of course 2012 see the announcement of the selection of thesis or graduation exam in the second semester of the academic year 2015-2016 here.</p>\r\n\r\n<p>Tp. HCM, January 12, 2016</p>\r\n\r\n<p>BCN Department of Physics - VLKT</p>\r\n', 'Notify V / v considered as thesis / graduation exam for students of 2012 class'),
(161, 92, 1, 'QUY ĐỊNH Học tiếng Anh và chuẩn trình độ tiếng Anh đối với sinh viên bậc đại học hệ chính quy', '<p>Sinh vi&ecirc;n xem quy định về chuẩn đầu ra v&agrave; học c&aacute;c học phần tiếng Anh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/tieng_anh_qui_dinh.pdf\">tại đ&acirc;y</a>&nbsp;hoặc tr&ecirc;n trang web trường/Ph&ograve;ng C&ocirc;ng t&aacute;c sinh vi&ecirc;n.</p>\r\n', 'QUY ĐỊNH Học tiếng Anh và chuẩn trình độ tiếng Anh đối với sinh viên bậc đại học hệ chính quy\r\n'),
(162, 92, 2, 'REGULATIONS Learning English and English proficiency standards for full-time undergraduate students', '<p>Students can view the requirements for learning English and study it here or on the school website / Student Affairs Department.<br />\r\n&nbsp;</p>\r\n', 'REGULATIONS Learning English and English proficiency standards for full-time undergraduate students\r\n'),
(163, 93, 1, 'Thông báo nộp hồ sơ tốt nghiệp đợt tháng 03/2016', '<p>Sinh vi&ecirc;n c&aacute;c kh&oacute;a 2011, 2010 xem th&ocirc;ng b&aacute;o x&eacute;t tốt nghiệp đợt th&aacute;ng 03/2016 tr&ecirc;n&nbsp;<a href=\"http://www.hcmus.edu.vn/images/stories/phong_dao_tao/15-16/hk2/xet_tn_t3.15_dhcq.pdf\">trang web trường</a>. Thời gian nộp đơn từ ng&agrave;y&nbsp;<strong>09-11/03/2016</strong>. Sinh vi&ecirc;n tải mẫu đơn&nbsp;<a href=\"http://www.hcmus.edu.vn/images/stories/phong_dao_tao/15-16/hk2/doncapbang_dh.doc\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>Th&ocirc;ng b&aacute;o ng&agrave;y 22/02/2016</p>\r\n\r\n<p>khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo nộp hồ sơ tốt nghiệp đợt tháng 03/2016\r\n'),
(164, 93, 2, 'Notice of graduation submission in March 2016', '<p>Students of courses 2011 and 2010 see the notice of graduation exam for March 2016 on the school website. Application time is from 09-11 March 2016. Students download the form here.</p>\r\n\r\n<p>Notice of February 22, 2016</p>\r\n\r\n<p>Department of Physics - VLKT</p>\r\n', 'Notice of graduation submission in March 2016\r\n'),
(165, 94, 1, 'Thông báo đăng ký học Cải thiện, học lại môn Hoãn thi', '<p>Sinh vi&ecirc;n xem th&ocirc;ng b&aacute;o:&nbsp;<a href=\"http://www.hcmus.edu.vn/images/stories/phong_dao_tao/15-16/hk2/thong_bao_dang_ky_mon_cai_thien_hoan_thi__hk2_15_16.pdf\">Đ</a><a href=\"http://www.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=9800&amp;Itemid=293\">ăng k&yacute; học Cải thiện, học lại m&ocirc;n Ho&atilde;n thi</a><a href=\"http://www.hcmus.edu.vn/images/stories/phong_dao_tao/15-16/hk2/thong_bao_dang_ky_mon_cai_thien_hoan_thi__hk2_15_16.pdf\">&nbsp;tr&ecirc;n trang web trường</a>.</p>\r\n\r\n<p>SInh vi&ecirc;n đọc kỹ th&ocirc;ng b&aacute;o trước khi đăng k&yacute; v&agrave; đăng k&yacute; đ&uacute;ng thời gian quy định.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n</ul>\r\n', 'Thông báo đăng ký học Cải thiện, học lại môn Hoãn thi\r\n'),
(166, 94, 2, 'Notice of Registration for Improving, Returning Exam', '<p>&nbsp;</p>\r\n\r\n<p>Students see the notice: Enrollment for Improving, repeating the deferred exam on the school website.<br />\r\n<br />\r\nStudents read the notice carefully before registering and register on time.</p>\r\n', 'Notice of Registration for Improving, Returning Exam'),
(167, 95, 1, 'Danh sách SV nhận học bổng Cựu sinh viên 2014', '<p>BCN Khoa Vật l&yacute; - Vật l&yacute; kỹ thuật th&ocirc;ng b&aacute;o c&aacute;c Sinh vi&ecirc;n c&oacute; t&ecirc;n trong danh s&aacute;ch (<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh_sach_nhan_HB_Cuu_SV_2014.jpg\">Quyết định đ&iacute;nh k&egrave;m</a>) sẽ nhận học bổng Cựu sinh vi&ecirc;n Khoa Vật l&yacute; v&agrave;o Ng&agrave;y truyền thống khoa 27-04-2014. Đề nghị c&aacute;c SV c&oacute; t&ecirc;n trong danh s&aacute;ch c&oacute; mặt tại Văn ph&ograve;ng Khoa Vật l&yacute; - VLKT trước 07 giờ 30 ng&agrave;y Chủ nhật 27-04 để ban tổ chức hướng dẫn v&agrave; sắp xếp chổ ngồi trong Giảng đường.&nbsp;<strong>&nbsp;Lưu &yacute;:</strong>&nbsp;SV phải ăn mặc lịch sự v&agrave; nghi&ecirc;m t&uacute;c.</p>\r\n\r\n<p>Ng&agrave;y 21-04-2014</p>\r\n\r\n<p>BCN Khoa Vật l&yacute; - VLKT</p>\r\n', 'Danh sách SV nhận học bổng Cựu sinh viên 2014\r\n'),
(168, 95, 2, 'List of students receiving alumni scholarships 2014', '<p>BCN Department of Physics - Technical Physics announces that the students named in the list (Attached Decision) will receive Alumni of the Faculty of Physics on the traditional day of the faculty April 27, 2014. Suggest the students named in the list to be present at the Office of the Department of Physics - VLKT before 07:30 on Sunday 27-04 to the organizers guide and arrange seats in the lecture hall. Note: Students must dress politely and seriously.</p>\r\n\r\n<p>April 21, 2014</p>\r\n\r\n<p>BCN Department of Physics - VLKT</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'List of students receiving alumni scholarships 2014\r\n'),
(169, 96, 1, 'Danh sách SV tốt nghiệp đợt tháng 03-2016', '<p><a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Tot%20nghiep/dstn_t3_2016_Vat_ly.pdf\">Danh s&aacute;ch SV tốt nghiệp đợt th&aacute;ng 3-2016</a></p>\r\n\r\n<p><a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Tot%20nghiep/ds_khong_tot_nghiep_dot_thang_3-2016.xls\">Danh s&aacute;ch kh&ocirc;ng đủ điều kiện tốt nghiệp</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Danh sách SV tốt nghiệp đợt tháng 03-2016\r\n'),
(170, 96, 2, 'List of graduating students in March 2016', '<p><a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Tot%20nghiep/dstn_t3_2016_Vat_ly.pdf\">Danh s&aacute;ch SV tốt nghiệp đợt th&aacute;ng 3-2016</a></p>\r\n\r\n<p><a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Tot%20nghiep/ds_khong_tot_nghiep_dot_thang_3-2016.xls\">Danh s&aacute;ch kh&ocirc;ng đủ điều kiện tốt nghiệp</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'List of graduating students in March 2016\r\n'),
(171, 97, 1, 'CÔNG TY TNHH BÌNH TIÊN ĐỒNG NAI TUYỂN DỤNG SINH VIÊN KHOA VẬT LÝ, VẬT LÝ TIN HỌC VÀ CÔNG NGHỆ THÔNG TIN', '<p>C&aacute;c em sinh vi&ecirc;n xem th&ocirc;ng tin tuyển dụng đăng tr&ecirc;n Fanpage Khoa Vật L&yacute;:</p>\r\n\r\n<p><a href=\"https://www.facebook.com/VLH2015/photos/a.514483052048284.1073741834.513260762170513/769828283180425/?type=3\">https://www.facebook.com/VLH2015/photos/a.514483052048284.1073741834.513260762170513/769828283180425/?type=3</a></p>\r\n\r\n<p>hoặc theo li&ecirc;n kết:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/0B6ue0CPI1AOFdWg0Ti13WWlPSGxQeWd3bGVadW9qd1Azb044/view?usp=sharing\">https://drive.google.com/file/d/0B6ue0CPI1AOFdWg0Ti13WWlPSGxQeWd3bGVadW9qd1Azb044/view?usp=sharing</a></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'CÔNG TY TNHH BÌNH TIÊN ĐỒNG NAI...'),
(172, 97, 2, 'BINH TIEN DONG NAI CO., LTD RECRUITS STUDENTS OF PHYSICS, INFORMATION TECHNOLOGY AND INFORMATION TECHNOLOGY STUDENTS', '<p>Students see the recruitment information posted on Fanpage of Physics Department:</p>\r\n\r\n<p><a href=\"https://www.facebook.com/VLH2015/photos/a.514483052048284.1073741834.513260762170513/769828283180425/?type=3\">https://www.facebook.com/VLH2015/photos/a.514483052048284.1073741834.513260762170513/769828283180425/?type=3</a></p>\r\n\r\n<p>or follow the link:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/0B6ue0CPI1AOFdWg0Ti13WWlPSGxQeWd3bGVadW9qd1Azb044/view?usp=sharing\">https://drive.google.com/file/d/0B6ue0CPI1AOFdWg0Ti13WWlPSGxQeWd3bGVadW9qd1Azb044/view?usp=sharing</a></p>\r\n', 'BINH TIEN DONG NAI CO., LTD RECRUITS STUDENTS OF PHYSICS, INFORMATION TECHNOLOGY AND INFORMATION TECHNOLOGY STUDENTS'),
(173, 98, 1, 'CÔNG TY CỔ PHẦN THÉP NAM KIM TUYỂN DỤNG', '<p>C&Ocirc;NG TY CỔ PHẦN TH&Eacute;P NAM KIM TUYỂN DỤNG<br />\r\n1.Đối tượng: Nam/ nữ<br />\r\n2.Thời gian l&agrave;m việc: Từ 8h00 - 17h00, Thứ Hai - Thứ Bảy<br />\r\n3.Địa điểm l&agrave;m việc: Đường N, Cụm Sản Xuất An Thạnh, Dĩ An, B&igrave;nh Dương.<br />\r\n4.Tr&igrave;nh Độ&nbsp;<br />\r\n-Y&ecirc;u cầu tr&igrave;nh độ Đại Học trở l&ecirc;n với chuy&ecirc;n ngh&agrave;nh C&ocirc;ng Nghệ Th&ocirc;ng Tin hoặc Khoa Học M&aacute;y T&iacute;nh hoặc ngh&agrave;nh kh&aacute;c nhưng c&oacute; đam m&ecirc; về c&ocirc;ng nghệ th&ocirc;ng tin.<br />\r\n-C&oacute; kiến thức về c&aacute;c database, computer programming, v&agrave; system administration. &nbsp;<br />\r\n-C&oacute; kiến thức về hệ thống ERP như Microsoft Dynamics AX.&nbsp;<br />\r\n-C&oacute; kiến thức về c&aacute;c c&aacute;ch thức kiểm tra giải ph&aacute;p phần mềm (Proof of Concept, Request for Proposal, X&acirc;y Dựng Kịch Bản Demo, Testing, etc.)&nbsp;<br />\r\n5.Kỹ năng&nbsp;<br />\r\n-Vẽ flow-chart c&aacute;c quy tr&igrave;nh của c&aacute;c ph&ograve;ng ban.&nbsp;<br />\r\n-Hướng dẫn người sử dụng hệ thống.&nbsp;<br />\r\n-Hiểu về cơ cấu một c&ocirc;ng ty sản xuất c&ocirc;ng nghiệp nặng.&nbsp;<br />\r\n-C&oacute; khả năng ph&acirc;n t&iacute;ch sự cố hoặc vấn đề trong hệ thống, v&agrave; c&oacute; thể đưa ra hướng giải quyết.&nbsp;<br />\r\n-L&agrave;m việc nh&oacute;m. &nbsp;<br />\r\n6.C&ocirc;ng Vi&ecirc;c Thực Tế&nbsp;<br />\r\n-Viết t&agrave;i liệu hướng dẫn sử dụng cho hệ thống c&aacute;c phần mềm tại Nam Kim.&nbsp;<br />\r\n-B&aacute;o c&aacute;o trực tiếp cho Gi&aacute;m Đốc Triển Khai ERP v&agrave; Trưởng Bộ Phận IT.&nbsp;<br />\r\n-Tham gia gi&aacute;m s&aacute;t triển khai c&aacute;c ph&acirc;n hệ trong hệ thống ERP.&nbsp;<br />\r\n-Quản l&yacute; c&aacute;c quy tr&igrave;nh nghiệp vụ v&agrave; giải quyết c&aacute;c vấn đề ph&aacute;t sinh.&nbsp;<br />\r\n-Kết hợp với ban ISO vẽ v&agrave; l&agrave;m lại &nbsp;c&aacute;c quy tr&igrave;nh c&aacute;c ph&ograve;ng ban.&nbsp;<br />\r\n-Tham gia quản trị hệ thống mạng, Server (AD, File, Mail,...) tại c&ocirc;ng ty.&nbsp;<br />\r\n-C&oacute; thể gi&agrave;nh thời gian buổi tối trực On-Call cho hệ thống theo lịch sắp xếp. &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n4.Quyền lợi&nbsp;<br />\r\n- Lương, thưởng, lương th&aacute;ng 13<br />\r\n- Cơm trưa ăn tại c&ocirc;ng ty.<br />\r\n- C&ocirc;ng ty tăng lương h&agrave;ng năm<br />\r\n- M&ocirc;i trường l&agrave;m việc: Năng động, th&acirc;n thi&ecirc;̣n, chuy&ecirc;n nghi&ecirc;̣p, cơ h&ocirc;̣i thăng ti&ecirc;́n cao<br />\r\n- Ng&agrave;y nghỉ: Theo quy định của Luật lao động<br />\r\n- Ph&uacute;c lợi:<br />\r\n+ Lương cạnh tranh, c&ocirc;ng bằng v&agrave; tạo động lực<br />\r\n+ Ch&iacute;nh s&aacute;ch thưởng, ph&uacute;c lợi đảm bảo theo năng lực v&agrave; vị tr&iacute;<br />\r\n+ Chế độ BHXH, BHYT, BHTN v&agrave; Bảo hiểm tai nạn 24/24<br />\r\n+ Kh&aacute;m sức khỏe định kỳ<br />\r\nĐể ứng tuyển, vui l&ograve;ng gửi CV bằng tiếng Anh hoặc Tiếng việt &nbsp;đến email:&nbsp;<br />\r\n<a href=\"mailto:tuyendung@namkimgroup.vn\">tuyendung@namkimgroup.vn</a>(Mr.Ph&uacute;c)<br />\r\nĐT: ( 0650) 3748 848 - 118<br />\r\nC&ocirc;ng ty cổ phần Th&eacute;p Nam Kim.<br />\r\nĐịa chỉ: Đường N1, Cụm Sản xuất An Thạnh, huyện Thuận An, B&igrave;nh Dương<br />\r\nXem bản gốc :<a href=\"https://drive.google.com/file/d/0B0yL-xLCx0Yfb2J1N2QxS0VmeGc/view?usp=sharing\">https://drive.google.com/file/d/0B0yL-xLCx0Yfb2J1N2QxS0VmeGc/view?usp=sharing</a></p>\r\n', 'CÔNG TY CỔ PHẦN THÉP NAM KIM TUYỂN DỤNG'),
(174, 98, 2, 'NAM KIM STEEL RECRUITMENT JOINT STOCK COMPANY', '<p>NAM KIM STEEL RECRUITMENT JOINT STOCK COMPANY<br />\r\n1. Audience: Male / female<br />\r\n2. Working time: From 8:00 am - 5:00 pm, Monday - Saturday<br />\r\n3. Working place: N Road, An Thanh Production Group, Di An, Binh Duong.<br />\r\n4. Qualification<br />\r\n- University or higher degree required with majors in Information Technology or Computer Science or other but passionate about information technology.<br />\r\n-Knowledge of databases, computer programming, and system administration.<br />\r\n-Knowledge of ERP systems such as Microsoft Dynamics AX.<br />\r\n- Knowledge of software solution testing (Proof of Concept, Request for Proposal, Demo Development, Testing, etc.)<br />\r\n5. Skill<br />\r\n-Draw the flow-chart of the processes of the departments.<br />\r\n-Instructions for system users.<br />\r\n-Understand the structure of a heavy industry manufacturing company.<br />\r\n-Ability to analyze problems or problems in the system, and can give solutions.<br />\r\n-Working group.<br />\r\n6. Practical Work<br />\r\n- Writing user manual for software system in Nam Kim.<br />\r\n-Report directly to ERP Implementation Director and Head of IT Department.<br />\r\n-Take part in supervising and deploying modules in the ERP system.<br />\r\n-Manage business processes and solve problems that arise.<br />\r\n-Combined with ISO board drawing and reworked departments processes.<br />\r\n-Take part in network management, Server (AD, File, Mail, ...) at the company.<br />\r\n-Can spend on-Call evening evenings for system scheduling.<br />\r\n4. Benefits<br />\r\n- Salary, bonus and 13th month salary<br />\r\n- Lunch at the company.<br />\r\n- The company raises annual salary<br />\r\n- Working environment: Dynamic, friendly, professional, high promotion opportunities<br />\r\n- Holidays: In accordance with the Labor Law<br />\r\n- Welfare:<br />\r\n+ Competitive, fair and motivating wages<br />\r\n+ Bonus and welfare policies ensured according to ability and location<br />\r\n+ Social insurance, health insurance, unemployment insurance and accident insurance 24/24<br />\r\n+ Periodic health examination<br />\r\nTo apply, please send your CV in English or Vietnamese to email:<br />\r\ntuyendung@namkimgroup.vn (Mr.Phuc)<br />\r\nTel: (0650) 3748 848 - 118<br />\r\nNam Kim Steel Joint Stock Company.<br />\r\nAddress: N1 Street, An Thạnh Production Cluster, Thuận An District, B&igrave;nh Dương<br />\r\nView original:&nbsp;<a href=\"https://drive.google.com/file/d/0B0yL-xLCx0Yfb2J1N2QxS0VmeGc/view?usp=sharing\">https://drive.google.com/file/d/0B0yL-xLCx0Yfb2J1N2QxS0VmeGc/view?usp=sharing</a></p>\r\n', 'NAM KIM STEEL RECRUITMENT JOINT STOCK COMPANY'),
(175, 99, 1, 'Công ty Arrive Technologies thông báo tuyển dụng', '<p>C&ocirc;ng ty Arrive Technologies th&ocirc;ng b&aacute;o tuyển dụng:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/Arrive_2017_1.jpg\" style=\"height:925px; width:750px\" /><img alt=\"\" src=\"/uploads/khoa-vat-ly/Arrive_2017_2.jpg\" style=\"height:1038px; width:750px\" /></p>\r\n', 'Công ty Arrive Technologies thông báo tuyển dụng'),
(176, 99, 2, 'Arrive Technologies, announces recruitment', '<p>Arrive Technologies, announces recruitment</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/Arrive_2017_1.jpg\" style=\"height:925px; width:750px\" /><img alt=\"\" src=\"/uploads/khoa-vat-ly/Arrive_2017_2.jpg\" style=\"height:1038px; width:750px\" /></p>\r\n', 'Arrive Technologies, announces recruitment'),
(177, 100, 1, 'Danh sách SV bị cảnh cáo và buộc thôi học', '<p>Sinh vi&ecirc;n Khoa Vật l&yacute; - Vật l&yacute; kỹ thuật xem danh s&aacute;ch SV bị cảnh c&aacute;o v&agrave; buộc th&ocirc;i học trong Học kỳ II năm học 2015-2016 (<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/NH%202015-2016/HK%20II/DS_Sinh_vien_bi_canh_cao_va_buoc_thoi_hoc_2016.pdf\">File đ&iacute;nh k&egrave;m</a>). Trường sẽ giải quyết mọi thắc của sinh vi&ecirc;n từ ng&agrave;y 28/03/2016 đến ng&agrave;y 08/04/2016.</p>\r\n\r\n<p>GV khoa Vật l&yacute; - VLKT</p>\r\n', 'Danh sách SV bị cảnh cáo và buộc thôi học\r\n'),
(178, 100, 2, 'List of students warned and forced to leave school', '<p>Students of the Faculty of Physics - Technical Physics see a list of students warned and forced to leave school in Semester II academic year 2015-2016 (Attached file). The school will handle all students&#39; questions from March 28, 2016 to April 8, 2016.</p>\r\n\r\n<p>Faculty of Physics - VLKT</p>\r\n', 'List of students warned and forced to leave school\r\n'),
(179, 101, 1, 'Lịch thi Học kỳ I năm học 2016-2017 (cập nhật)', '<p>Sinh vi&ecirc;n xem lịch thi HK I năm học 2016-2017 tr&ecirc;n trang web của Ph&ograve;ng đ&agrave;o tạo:</p>\r\n\r\n<p>-&nbsp;<strong>Lịch thi c&aacute;c m&ocirc;n chuy&ecirc;n ng&agrave;nh v&agrave; cơ sở ng&agrave;nh c&oacute; cập nhật bổ sung</strong>&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Lich%20thi/Lich_thi_HK_I_2016-2017_Vat_ly_va_KTHN.xls\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>-&nbsp;<a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=10898&amp;Itemid=417\">Lịch thi cuối kỳ HK1/16-17 hệ ĐHCQ tại cơ sở Linh Trung (Kh&oacute;a 2015 v&agrave; 2016)</a></p>\r\n\r\n<p><strong><u>Lưu &yacute;:</u></strong>&nbsp;Sinh vi&ecirc;n thường xuy&ecirc;n theo d&otilde;i lịch thi để cập nhật c&aacute;c thay đổi kịp thời</p>\r\n\r\n<p>- Sinh vi&ecirc;n c&oacute; mặt tại ph&ograve;ng thi trước giờ thi 15 ph&uacute;t, SV phải mang theo thẻ SV v&agrave; CMND.</p>\r\n\r\n<p>-&nbsp; Ng&agrave;y 29/12/2016, sinh vi&ecirc;n xem lịch thi - ph&ograve;ng thi chi tiết tr&ecirc;n web trường v&agrave; trong t&agrave;i khoản Portal.</p>\r\n', 'Lịch thi Học kỳ I năm học 2016-2017 (cập nhật)\r\n'),
(180, 101, 2, 'Test schedule for Semester I of the 2016-2017 school year (updated)', '<p>Students see HK I exam schedule for the academic year 2016-2017 on the website of the Training Department:</p>\r\n\r\n<p>- The exam schedule for specialized subjects and branch facilities is updated here.</p>\r\n\r\n<p>- Final exam schedule for HK1 / 16-17 system of General Assembly at Linh Trung Campus (Course 2015 and 2016)</p>\r\n\r\n<p>Note: Students regularly monitor exam schedules to update changes promptly</p>\r\n\r\n<p>- Students present at the examination room 15 minutes before the exam time, students must bring student ID card and ID card.</p>\r\n\r\n<p>- On December 29, 2016, students viewed exam schedule - exam room details on the school website and in the Portal account.</p>\r\n', 'Test schedule for Semester I of the 2016-2017 school year (updated)'),
(181, 102, 1, 'Công ty Mitsuba tuyển dụng sinh viên Khoa Vật Lý đã tốt nghiệp (hạn chót 22/3)', '<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/WP_20170310_10_04_48_Pro.jpg\" style=\"height:966px; width:750px\" /></p>\r\n', 'Công ty Mitsuba tuyển dụng sinh viên...'),
(182, 102, 2, 'Mitsuba Company hires graduates of Physics Department (March 22)', '<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/WP_20170310_10_04_48_Pro.jpg\" style=\"height:966px; width:750px\" /></p>\r\n', 'Mitsuba Company hires graduates of Physics Department (March 22)'),
(183, 103, 1, 'Thông báo tuyển dụng của tập đoàn Bosch trong tháng 5', '<p>Th&ocirc;ng b&aacute;o tuyển dụng của tập đo&agrave;n Bosch trong th&aacute;ng 5:<br />\r\nSinh vi&ecirc;n lưu &yacute;: C&aacute;c vị tr&iacute; c&oacute; ch&uacute; th&iacute;ch URGENT l&agrave; những vị tr&iacute; cần tuyển gấp.<br />\r\n<a href=\"https://drive.google.com/file/d/0B0yL-xLCx0YfbzZKMnRBaUNmdE0/view?usp=sharing\">https://drive.google.com/file/d/0B0yL-xLCx0YfbzZKMnRBaUNmdE0/view?usp=sharing</a></p>\r\n', 'Thông báo tuyển dụng của tập đoàn Bosch...'),
(184, 103, 2, 'Employment announcement of Bosch Group in May', '<p>Notice of recruitment of the Bosch Group in May:<br />\r\nNote: URGENT-annotated positions are urgent vacancies.<br />\r\n<a href=\"https://drive.google.com/file/d/0B0yL-xLCx0YfbzZKMnRBaUNmdE0/view?usp=sharing\">https://drive.google.com/file/d/0B0yL-xLCx0YfbzZKMnRBaUNmdE0/view?usp=sharing</a></p>\r\n', 'Employment announcement of Bosch Group in May'),
(185, 104, 1, 'Yuanch Việt Nam thông báo tuyển dụng', '<p>C&ocirc;ng ty TNHH Vật tư QUANG HỌC Yuanch Việt Nam th&ocirc;ng b&aacute;o tuyển dụng:<br />\r\nYuanch Vi&ecirc;̣t Nam là c&ocirc;ng ty thương mại cung ứng v&acirc;̣t tư ti&ecirc;u hao, thi&ecirc;́t bị đo đạc, máy móc ngành quang học. Các sản ph&acirc;̉m được nh&acirc;̣p kh&acirc;̉u từ Nh&acirc;̣t, Anh, Mỹ và Trung Qu&ocirc;́c đáp ứng nhu c&acirc;̀u v&acirc;̣t tư từ trung đ&ecirc;́n cao c&acirc;́p cho các nhà sản xu&acirc;́t và gia c&ocirc;ng th&acirc;́u kính chính xác, tròng kính mắt, ...<br />\r\nĐịa chỉ c&ocirc;ng ty: L&acirc;̀u 7, Tòa nhà Mekong Tower, 235-241 C&ocirc;̣ng Hòa, P.13, Q. T&acirc;n Bình, TP. HCM<br />\r\nWebsite: <a href=\"http://www.yuanch.com/\">www.yuanch.com</a><br />\r\nHiện tại c&ocirc;ng ty đang cần tuyển vị tr&iacute; Chuy&ecirc;n vi&ecirc;n kinh doanh, sinh vi&ecirc;n xem th&ocirc;ng tin chi tiết trong tệp đ&iacute;nh k&egrave;m:<a href=\"https://drive.google.com/file/d/0B6ue0CPI1AOFbk9uMElWNUowems/view\">https://drive.google.com/file/d/0B6ue0CPI1AOFbk9uMElWNUowems/view</a></p>\r\n', 'Yuanch Việt Nam thông báo tuyển dụng'),
(186, 104, 2, 'Yuanch Vietnam announces recruitment', '<p>Yuanch Vietnam Optical Materials Co., Ltd announces recruitment:<br />\r\nYuanch Vietnam is a trading company supplying consumables, measuring equipment, optical machines. The products are imported from Japan, England, USA and China to meet the needs of medium to high-quality materials for manufacturers and processors of precision lenses, eyeglasses,<br />\r\nAddress: 7th Floor, Mekong Tower Building, 235-241 Cong Hoa, Ward 13, Tan Binh District, Ho Chi Minh City HCM<br />\r\nWebsite: <a href=\"http://www.yuanch.com/\">www.yuanch.com</a><br />\r\nCurrently, the company is looking for a position of a Sales Executive, students see details in the attached file:<a href=\"https://drive.google.com/file/d/0B6ue0CPI1AOFbk9uMElWNUowems/view\">https://drive.google.com/file/d/0B6ue0CPI1AOFbk9uMElWNUowems/view</a></p>\r\n', 'Yuanch Vietnam announces recruitment'),
(187, 105, 1, 'Thông báo đăng ký học phần qua mạng HK II năm học 2016-2017', '<p>Sinh vi&ecirc;n xem th&ocirc;ng b&aacute;o đăng k&yacute; học phần qua mạng HK II năm học 2016-2017 tr&ecirc;n&nbsp;<a href=\"http://web.hcmus.edu.vn/images/stories/phong_dao_tao/16-17/hk2/tb_dkhp_hk2_16_17.pdf\">trang web ph&ograve;ng đ&agrave;o tạo</a>.</p>\r\n\r\n<p>Sinh vi&ecirc;n lưu &yacute; khi đăng k&yacute; học phần:</p>\r\n\r\n<p>- Thời gian đăng k&yacute; học phần từ 08h00 ng&agrave;y 15/02/2017 đến 18h00 ng&agrave;y 18/02/2017.</p>\r\n\r\n<p>-&nbsp;Từ HK II n&agrave;y, SV chỉ được ph&eacute;p đăng k&yacute; tối đa 25 t&iacute;n chỉ trong học kỳ, bao gồm cả học phần trả nợ. Sinh vi&ecirc;n đăng k&yacute; học trả nợ c&ugrave;ng đợt đăng k&yacute; ch&iacute;nh thức (Kh&ocirc;ng c&oacute; đợt đăng k&yacute; trả nợ ri&ecirc;ng). Đối với đăng k&yacute; cải thiện, sinh vi&ecirc;n chờ xem th&ocirc;ng b&aacute;o của Ph&ograve;ng Đ&agrave;o tạo.</p>\r\n\r\n<p>- Sinh vi&ecirc;n xem thời kh&oacute;a biểu c&aacute;c m&ocirc;n đại cương tr&ecirc;n trang web ph&ograve;ng đ&agrave;o tạo.</p>\r\n\r\n<p>- Sinh vi&ecirc;n xem thời kh&oacute;a biểu c&aacute;c m&ocirc;n chung v&agrave; m&ocirc;n chuy&ecirc;n ng&agrave;nh tr&ecirc;n trang web khoa Vật l&yacute; - Vật l&yacute; kỹ thuật.</p>\r\n\r\n<p>- Sinh vi&ecirc;n kh&oacute;a 2013 xem danh s&aacute;ch x&eacute;t l&agrave;m kh&oacute;a luận hoặc thi tốt nghiệp tại bộ m&ocirc;n để biết m&igrave;nh được đăng k&yacute; h&igrave;nh thức n&agrave;o trước khi đăng k&yacute; học phần tr&ecirc;n mạng. SV đăng k&yacute; học phần Kh&oacute;a luận tốt nghiệp hoặc 3 học phần thi tốt nghiệp như trong thời kh&oacute;a biểu của khoa.</p>\r\n\r\n<p>Trong thời gian đăng k&yacute; học phần, sinh vi&ecirc;n cần hỗ trợ c&oacute; thể li&ecirc;n hệ Gi&aacute;o vụ khoa để được giải quyết. Khoa chỉ giải quyết c&aacute;c vần đề đăng k&yacute; học phần của sinh vi&ecirc;n đến 16h30 ng&agrave;y 24/02/2017.</p>\r\n\r\n<p>Sinh vi&ecirc;n n&ecirc;n lưu lại kết quả đăng k&yacute; để l&agrave;m minh chứng đối chiếu sau n&agrave;y.</p>\r\n\r\n<p>Gi&aacute;o vụ khoa Vật l&yacute; - Vật l&yacute; kỹ thuật</p>\r\n', 'Thông báo đăng ký học phần qua mạng HK II năm học 2016-2017\r\n'),
(188, 105, 2, 'Notice of online registration of HK II modules for the academic year 2016-2017', '<p>Students see the notice of online registration of HK II for the 2016-2017 academic year on the training room website.</p>\r\n\r\n<p>Students note when registering for the module:</p>\r\n\r\n<p>- Registration time is from 08:00 on February 15, 2017 to 6:00 pm on February 18, 2017.</p>\r\n\r\n<p>- From this HK II, students are only allowed to register up to 25 credits in the semester, including the repayment module. Students register for loan repayment with the official registration (There is no separate repayment schedule). For improved registration, students wait to see the notice of the Training Department.</p>\r\n\r\n<p>- Students view the general subjects timetable on the training room website.</p>\r\n\r\n<p>- Students view the schedule of general subjects and specialized subjects on the website of Department of Physics - Technical Physics.</p>\r\n\r\n<p>- Students of class 2013 see the list of essay review or graduation exam in the subject to know what form they are allowed to register before taking the online module. Students register for the Graduation thesis module or 3 graduation modules as in the schedule of the department.</p>\r\n\r\n<p>During the time of registration, students who need support can contact the Faculty Department to be resolved. The department only handles students&#39; registration issues until 4:30 PM on February 24, 2017.</p>\r\n\r\n<p>Students should save the results of the registration for future reference.</p>\r\n\r\n<p>Department of Physics - Technical Physics</p>\r\n', 'Notice of online registration of HK II modules for the academic year 2016-2017\r\n'),
(189, 106, 1, 'Tập đoàn Horiba thông báo tuyển dụng', '<p>Tập đo&agrave;n Horiba th&ocirc;ng b&aacute;o tuyển dụng<br />\r\nHiện tại C&ocirc;ng ty Horiba Vietnam đang cần tuyển một Ứng vi&ecirc;n phụ tr&aacute;ch sale Kỹ thuật tại miền Nam với những th&ocirc;ng tin cụ thể như sau:<br />\r\n-Y&ecirc;u cầu: tốt nghiệp ng&agrave;nh Vật l&yacute;, H&oacute;a, Vật liệu. C&oacute; hiểu biết về c&aacute;c Thiết bị đo t&iacute;nh chất Vật l&yacute; v&agrave; H&oacute;a L&yacute;.<br />\r\n-Giới t&iacute;nh: Nam<br />\r\n-Tiếng Anh: tốt<br />\r\n-C&oacute; kinh nghiệm, ngoại h&igrave;nh v&agrave; khả năng giao tiếp sẽ ưu ti&ecirc;n.<br />\r\n-Lương khởi điểm : TR&Ecirc;N 10.000.000đ/th&aacute;ng<br />\r\nSẽ c&oacute; c&aacute;c phụ cấp kh&aacute;c sau khi thử việc.<br />\r\nC&aacute;c em sinh vi&ecirc;n quan t&acirc;m xin nộp hồ sơ cho C&ocirc; Ngọc Anh,<br />\r\nEmail:&nbsp;<a href=\"mailto:ngocanh.pham@horiba.com\">ngocanh.pham@horiba.com</a><br />\r\nTham khảo c&aacute;c th&ocirc;ng b&aacute;o tuyển dụng kh&aacute;c:<a href=\"https://www.facebook.com/VLH2015/posts/878629182300334\">https://www.facebook.com/VLH2015/posts/878629182300334</a></p>\r\n', 'Tập đoàn Horiba thông báo tuyển dụng'),
(190, 106, 2, 'Horiba Group announces recruitment', '<p>Horiba Group announces recruitment<br />\r\nCurrently, Horiba Vietnam is seeking a Technical Sales candidate in the South with the following information:<br />\r\n-Requirements: graduate in Physics, Chemistry, Materials. Having knowledge of Physical and Physical Chemistry Instruments.<br />\r\n-Gender: Male<br />\r\n-Good English<br />\r\n-Experience, appearance and ability to communicate will take precedence.<br />\r\n- Starting salary: VND 10,000,000 / month<br />\r\nThere will be other allowances after the probation.<br />\r\nInterested students please apply for Ms. Ngoc Anh,<br />\r\nEmail:&nbsp;<a href=\"mailto:ngocanh.pham@horiba.com\">ngocanh.pham@horiba.com</a><br />\r\nRefer to other recruitment notices:<a href=\"https://www.facebook.com/VLH2015/posts/878629182300334\">https://www.facebook.com/VLH2015/posts/878629182300334</a></p>\r\n', 'Horiba Group announces recruitment'),
(191, 107, 1, 'Thông báo Về việc chấm phúc khảo kỳ thi học kỳ 1 năm học 2016-2017', '<p>Sinh vi&ecirc;n xem th&ocirc;ng b&aacute;o Về việc chấm ph&uacute;c khảo kỳ thi học kỳ 1 năm học 2016-2017 tr&ecirc;n&nbsp;<a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=11056&amp;Itemid=417\">trang web trường</a>.</p>\r\n\r\n<p>Thời gian nộp đơn từ ng&agrave;y 20-23/02/2017. Sinh vi&ecirc;n ch&uacute; &yacute; nộp đ&uacute;ng thời gian n&agrave;y.</p>\r\n\r\n<p>Địa điểm nộp đơn: Ph&ograve;ng Khảo th&iacute; (kế b&ecirc;n Ph&ograve;ng đ&agrave;o tạo) tại cơ sở Nguyễn Văn Cừ hoặc Linh Trung.</p>\r\n\r\n<p>Sinh vi&ecirc;n lưu &yacute;: Từ năm học 2016-2017, sinh vi&ecirc;n&nbsp;kh&ocirc;ng nộp đơn ph&uacute;c khảo tại văn ph&ograve;ng khoa hoặc ph&ograve;ng đ&agrave;o tạo.&nbsp;Mọi khiếu nại, thắc mắc về điểm thi, sinh vi&ecirc;n li&ecirc;n hệ Ph&ograve;ng Khảo th&iacute;.</p>\r\n\r\n<p>Gi&aacute;o vụ khoa Vật l&yacute; - VLKT</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'Thông báo Về việc chấm phúc khảo kỳ thi học kỳ 1 năm học 2016-2017\r\n'),
(192, 107, 2, 'Announcement Regarding the evaluation of the 1st semester of the 2016-2017 school year', '<p>Students see the notice of the review of the 1st semester of 2016-2017 exam on the school website.</p>\r\n\r\n<p>Application time is from February 20-23, 2017. Students pay attention this time.</p>\r\n\r\n<p>Location of application: Testing room (next to Training room) at Nguyen Van Cu or Linh Trung campus.</p>\r\n\r\n<p>Students note: From the 2016-2017 school year, students do not apply for re-examination at the faculty office or training room. All complaints, questions about test scores, students contact the Testing Room.</p>\r\n\r\n<p>Department of Physics - VLKT</p>\r\n', 'Announcement Regarding the evaluation of the 1st semester of the 2016-2017 school year'),
(193, 108, 1, 'Công ty Mitsuba thông báo tuyển dụng sinh viên khoa Vật Lý sắp tốt nghiệp', '<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/THONG_TIN_TUYEN_DUNG_MITSUBA9-2017.jpg\" style=\"height:971px; width:750px\" /></p>\r\n', 'Công ty Mitsuba thông báo tuyển dụng sinh viên..'),
(194, 108, 2, 'Mitsuba Company announces recruitment of graduates of Physics Department', '<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/THONG_TIN_TUYEN_DUNG_MITSUBA9-2017.jpg\" style=\"height:971px; width:750px\" /></p>\r\n', 'Mitsuba Company announces recruitment of graduates of Physics Department'),
(195, 109, 1, 'Thông báo đổi lịch học môn Điện tử cơ bản và Vật lý nguyên tử', '<p>Khoa Vật l&yacute; - Vật l&yacute; kỹ thuật th&ocirc;ng b&aacute;o sinh vi&ecirc;n học m&ocirc;n Điện tử cơ bản v&agrave; m&ocirc;n Vật l&yacute; nguy&ecirc;n tử về việc thay đổi lịch học như sau:</p>\r\n\r\n<p>- Sinh vi&ecirc;n học m&ocirc;n&nbsp;<strong>Điện tử cơ bản v&agrave;o s&aacute;ng thứ s&aacute;u sẽ chuyển sang học v&agrave;o chiều thứ Ba (tiết 6-9) tại ph&ograve;ng F304</strong></p>\r\n\r\n<p>- Sinh vi&ecirc;n học m&ocirc;n&nbsp;<strong>Vật l&yacute; nguy&ecirc;n tử v&agrave;o chiều thứ Ba sẽ chuyển sang học v&agrave;o s&aacute;ng thứ S&aacute;u (tiết 1-3) tại ph&ograve;ng F305</strong></p>\r\n\r\n<p><strong>Cập nhật lại: Thời gian thay đổi lịch học đến khi kết th&uacute;c học kỳ</strong>.</p>\r\n\r\n<p>V&igrave; l&yacute; do GV dạy m&ocirc;n Điện tử cơ bản bận c&ocirc;ng t&aacute;c n&ecirc;n kh&ocirc;ng dạy được v&agrave;o s&aacute;ng thứ s&aacute;u trong thời gian như tr&ecirc;n, rất mong sinh vi&ecirc;n th&ocirc;ng cảm sắp xếp đi học theo đ&uacute;ng lịch như tr&ecirc;n.</p>\r\n\r\n<p>Tp. HCM, ng&agrave;y 28 th&aacute;ng 02 năm 2017</p>\r\n\r\n<p>BCN Khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo đổi lịch học môn Điện tử cơ bản và Vật lý nguyên tử\r\n'),
(196, 109, 2, 'Notice of changing the schedule of basic electronics and atomic physics', '<p>The Faculty of Physics - Technical Physics informs students of Basic Electronics and Atomic Physics courses on changing schedule as follows:</p>\r\n\r\n<p>- Students studying Basic Electronics on Friday morning will move to study on Tuesday afternoon (periods 6-9) in room F304</p>\r\n\r\n<p>- Students studying Atomic Physics on Tuesday afternoon will move to study on Friday morning (periods 1-3) in room F305</p>\r\n\r\n<p>Update: The schedule changes to the end of the semester.</p>\r\n\r\n<p>Because of the reason that the lecturer who teaches basic electronics, he is unable to teach on Friday morning during the above time, I hope students sympathize with the schedule.</p>\r\n\r\n<p>Tp. HCM, February 28, 2017</p>\r\n\r\n<p>BCN Department of Physics - VLKT</p>\r\n', 'Notice of changing the schedule of basic electronics and atomic physics'),
(197, 110, 1, 'Arrive Technologies tuyển 5 kỹ sư Design Validation', '<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/Jobads-_DV_Engineers-Nov_2017-page-001.jpg\" style=\"height:971px; width:750px\" /><img alt=\"\" src=\"/uploads/khoa-vat-ly/Jobads-_DV_Engineers-Nov_2017-page-002.jpg\" style=\"height:971px; width:750px\" /></p>\r\n', 'Arrive Technologies tuyển 5 kỹ sư Design Validation'),
(198, 110, 2, 'Arrive Technologies employs 5 Design Validation Engineers', '<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/Jobads-_DV_Engineers-Nov_2017-page-001.jpg\" style=\"height:971px; width:750px\" /><img alt=\"\" src=\"/uploads/khoa-vat-ly/Jobads-_DV_Engineers-Nov_2017-page-002.jpg\" style=\"height:971px; width:750px\" /></p>\r\n', 'Arrive Technologies employs 5 Design Validation Engineers'),
(199, 111, 1, 'Danh sách sinh viên học TH Điện tử cơ bản và Phương pháp tính', '<p>Sinh vi&ecirc;n xem danh s&aacute;ch chia nh&oacute;m Thực h&agrave;nh m&ocirc;n Điện tử cơ bản xem trong<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Thoi%20khoa%20bieu/HK%20II%202016-2017/DS_SINH_VIEN_TH_Dien_tu_co_ban.pdf\">&nbsp;file đ&iacute;nh k&egrave;m</a>. Sinh vi&ecirc;n đến ph&ograve;ng E304 mua t&agrave;i liệu v&agrave; chia nh&oacute;m thực h&agrave;nh. 1/2 nh&oacute;m sẽ học tại ph&ograve;ng E306, phần c&ograve;n lại học tại ph&ograve;ng E303.</p>\r\n\r\n<p>Sinh vi&ecirc;n xem danh s&aacute;ch chia nh&oacute;m Thực h&agrave;nh m&ocirc;n Phương ph&aacute;p t&iacute;nh xem trong&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Thoi%20khoa%20bieu/HK%20II%202016-2017/DS_SINH_VIEN_TH_Phuong_phap_tinh.pdf\">file đ&iacute;nh k&egrave;m</a>.</p>\r\n\r\n<p>Sinh vi&ecirc;n chưa đăng k&yacute; nh&oacute;m đến đăng k&yacute; nh&oacute;m học với GV tại ph&ograve;ng th&iacute; nghiệm.</p>\r\n', 'Danh sách sinh viên học TH Điện tử cơ bản và Phương pháp tính\r\n'),
(200, 111, 2, 'List of students studying Basic Electronics and Calculating Methods', '<p>Students see the list of groups divided into Basic Electronics Practice in the attached file. Students come to E304 room to buy materials and practice group. Half of the group will study in room E306, the rest will study in room E303.</p>\r\n\r\n<p>Students see the list of groups. Practice Method Calculation method in attachment.</p>\r\n\r\n<p>Unregistered students come to register for a study group with teachers at the lab.</p>\r\n', 'List of students studying Basic Electronics and Calculating Methods'),
(201, 112, 1, 'Tuyển dụng kỹ sư cho nhiều vị trí tại công ty Olympus', '<p>Tuyển dụng kỹ sư cho nhiều vị tr&iacute; tại c&ocirc;ng ty Olympus</p>\r\n\r\n<p>Olympus l&agrave; c&ocirc;ng ty h&agrave;ng đầu nhật bản chuy&ecirc;n về m&aacute;y ảnh, k&iacute;nh hiển vi, thiết bị c&ocirc;ng nghiệp v&agrave; y tế. Hiện nay, ch&uacute;ng t&ocirc;i đang mở rộng nh&agrave; m&aacute;y tại khu c&ocirc;ng nghiệp Long Th&agrave;nh, tỉnh Đồng Nai.</p>\r\n\r\n<p>Chi tiết c&ocirc;ng việc<br />\r\nDo nhu cầu mở rộng sản xuất, nh&agrave; m&aacute;y Olympus cần tuyển nhiều vị tr&iacute; nh&acirc;n vi&ecirc;n Kỹ thuật<br />\r\n- Quản l&yacute; v&agrave; đ&aacute;nh gi&aacute; chất lượng c&ocirc;ng đoạn sản xuất gia c&ocirc;ng thấu k&iacute;nh d&ugrave;ng trong m&aacute;y ảnh kỹ thuật số,<br />\r\n- Quản l&yacute; sản xuất; Quản l&yacute; c&ocirc;ng đoạn sản xuất linh kiện thấu k&iacute;nh,<br />\r\n- Đối ứng thiết kế, lắp đặt v&agrave; cải tiến c&ocirc;ng đoạn sản xuất sản phẩm mới,<br />\r\n- T&igrave;m lỗi v&agrave; đưa ra giải ph&aacute;p khắc phục lỗi sản phẩm,<br />\r\n- Thiết lập quy tr&igrave;nh sản xuất sản phẩm mới,<br />\r\n- Đối ứng đảm bảo QCD c&aacute;c d&ograve;ng sản phẩm đang sản xuất.</p>\r\n\r\n<p>* Quyền lợi được hưởng:<br />\r\n- Trợ cấp ng&ocirc;n ngữ hấp dẫn (Tiếng Nhật v&agrave; tiếng Anh từ 700,000đ cho đến 6,000,000đ / th&aacute;ng).<br />\r\n- L&agrave;m việc từ thứ Hai đến thứ S&aacute;u h&agrave;ng tuần v&agrave; một ng&agrave;y thứ Bảy trong th&aacute;ng.<br />\r\n- Thưởng l&agrave;m việc chuy&ecirc;n cần; thưởng th&acirc;m ni&ecirc;n v&agrave; thưởng Tết<br />\r\n- Cơ hội thăng tiến l&ecirc;n cấp quản l&yacute;. M&ocirc;i trường l&agrave;m việc chuy&ecirc;n nghiệp<br />\r\n- C&aacute;c kh&oacute;a đ&agrave;o tạo n&acirc;ng cao kỹ năng mềm, kỹ năng l&agrave;m việc hiệu quả, kỹ năng quản l&yacute;&hellip;<br />\r\n- Chế độ nghỉ ph&eacute;p năm: cứ mỗi 3 năm l&agrave;m việc li&ecirc;n tục sẽ được tăng th&ecirc;m 1 ng&agrave;y ph&eacute;p năm,<br />\r\n- Du lịch, qu&agrave; tặng c&aacute;c dịp lễ Tết v&agrave; tiệc h&agrave;ng năm (tiệc T&acirc;n ni&ecirc;n, ng&agrave;y Phụ nữ Việt Nam/ tiệc Gi&aacute;ng sinh&hellip;).<br />\r\n- Chăm s&oacute;c sức khỏe cho c&ocirc;ng nh&acirc;n vi&ecirc;n h&agrave;ng năm.<br />\r\n- C&oacute; xe đưa rước nh&acirc;n vi&ecirc;n từ Tp. HCM, Bi&ecirc;n H&ograve;a đến c&ocirc;ng ty.</p>\r\n\r\n<p>Y&Ecirc;U CẦU C&Ocirc;NG VIỆC</p>\r\n\r\n<p>- Tuổi từ 22 đến 28.<br />\r\n- Tốt nghiệp Đại học chuy&ecirc;n ng&agrave;nh Vật l&yacute;/H&oacute;a học/Khoa học vật liệu hoặc Điện/đi&ecirc;n tử<br />\r\n- C&oacute; khả năng đọc bản vẽ kỹ thuật,<br />\r\n- Sử dụng được chương tr&igrave;nh Microsoft Office,<br />\r\n- C&oacute; thể đi c&ocirc;ng t&aacute;c nước ngo&agrave;i<br />\r\n- C&oacute; thể l&agrave;m việc theo ca, tăng ca,<br />\r\n- C&oacute; tinh thần học hỏi: kỹ thuật, tiếng Nhật, tiếng Hoa....<br />\r\n- Vui l&ograve;ng gởi CV bằng file PDF hoặc Word v&agrave;o email:<br />\r\n<a href=\"mailto:thuanviethcmus@gmail.com\">thuanviethcmus@gmail.com</a>&nbsp;hoặc&nbsp;<a href=\"mailto:viet_caoduc@olympus-vn.com\">viet_caoduc@olympus-vn.com</a></p>\r\n', 'Tuyển dụng kỹ sư cho nhiều vị trí tại công ty Olympus');
INSERT INTO `posts_lang` (`id`, `post_id`, `lang_id`, `title`, `content`, `excerpt`) VALUES
(202, 112, 2, 'Recruiting engineers for many positions at Olympus company', '<p>Recruiting engineers for many positions at Olympus company</p>\r\n\r\n<p>Olympus is a leading Japanese company specializing in cameras, microscopes, industrial and medical equipment. Currently, we are expanding the factory in Long Thanh industrial park, Dong Nai province.</p>\r\n\r\n<p>Job details<br />\r\nDue to the need to expand production, Olympus factory needs to recruit many positions of Technical staff<br />\r\n- Managing and evaluating the quality of lens manufacturing processes used in digital cameras,<br />\r\n- Production manager; Managing the production of lens components,<br />\r\n- Reciprocal design, installation and improvement of manufacturing stages of new products,<br />\r\n- Find errors and offer solutions to fix product errors,<br />\r\n- Establish production process for new products,<br />\r\n- Corresponding to ensure QCD the product lines are manufactured.</p>\r\n\r\n<p>* Benefit:<br />\r\n- Attractive language allowance (Japanese and English from VND 700,000 to VND 6,000,000 / month).<br />\r\n- Working from Monday to Friday weekly and one Saturday in the month.<br />\r\n- Reward for hard work; seniority bonus and Tet bonus<br />\r\n- Promotion opportunities to management level. Professional working environment<br />\r\n- Training courses to improve soft skills, effective working skills, management skills ...<br />\r\n- Annual leave: every 3 years of continuous working will be increased by 1 day annual leave,<br />\r\n- Tourism, gifts for Tet and annual parties (New Year&#39;s Eve, Vietnamese Women&#39;s Day / Christmas party ...).<br />\r\n- Health care for employees annually.<br />\r\n- There is a shuttle to and from the city. HCM, Bien Hoa to the company.</p>\r\n\r\n<p>JOB REQUIREMENTS</p>\r\n\r\n<p>- Age from 22 to 28.<br />\r\n- University degree in Physics / Chemistry / Materials Science or Electronics / Electronics<br />\r\n- Ability to read technical drawings,<br />\r\n- Use the Microsoft Office program,<br />\r\n- Can go on business abroad<br />\r\n- Can work in shifts, overtime,<br />\r\n- Having a spirit of learning: technology, Japanese, Chinese ....<br />\r\n- Please send your CV in PDF or Word to email:</p>\r\n\r\n<p><a href=\"mailto:thuanviethcmus@gmail.com\">thuanviethcmus@gmail.com</a>&nbsp;or&nbsp;<a href=\"mailto:viet_caoduc@olympus-vn.com\">viet_caoduc@olympus-vn.com</a></p>\r\n', 'Recruiting engineers for many positions at Olympus company'),
(203, 113, 1, 'Quy định về học tiếng anh và chuẩn trình độ tiếng anh', '<p>Quy định học tiếng anh v&agrave; Chuẩn tr&igrave;nh độ tiếng anh đối với c&aacute;c&nbsp;<a href=\"http://web.hcmus.edu.vn/images/stories/phong_dao_tao/16-17/hk1/quy_dinh_ta_k13_14_15.pdf\">kh&oacute;a 2013, 2014, 2015</a>.</p>\r\n\r\n<p>Quy định học tiếng anh v&agrave; Chuẩn tr&igrave;nh độ tiếng anh đối với c&aacute;c&nbsp;<a href=\"http://web.hcmus.edu.vn/images/stories/phong_dao_tao/16-17/hk1/quy_dinh_ta_k2016.pdf\">kh&oacute;a 2016</a>.</p>\r\n', 'Quy định về học tiếng anh và chuẩn trình độ tiếng anh\r\n'),
(204, 113, 2, 'Regulations on learning English and standard of English', '<p>Regulations on learning English and English proficiency standards for the courses 2013, 2014, 2015.</p>\r\n\r\n<p>Regulations on learning English and English proficiency standards for 2016 courses.</p>\r\n', 'Regulations on learning English and standard of English'),
(205, 114, 1, 'Thông báo đăng ký học phần HK I năm học 2017-2018', '<p>Sinh vi&ecirc;n xem th&ocirc;ng b&aacute;o đăng k&yacute; học phần học kỳ I năm học 2017-2018 tr&ecirc;n trang web trường. Thời gian đăng k&yacute; từ ng&agrave;y 21-27/08/2017. Sinh vi&ecirc;n lưu &yacute;: Sinh vi&ecirc;n đăng k&yacute; học c&aacute;c m&ocirc;n trong học kỳ ch&iacute;nh v&agrave; học trả nợ trong thời gian đăng k&yacute; ch&iacute;nh thức n&agrave;y (Kh&ocirc;ng c&oacute; đợt đăng k&yacute; học trả nợ ri&ecirc;ng). Sinh vi&ecirc;n cần hỗ trợ v&agrave; tư vấn c&oacute; thể li&ecirc;n hệ gi&aacute;o vụ khoa (Thầy Nhẫn) bằng c&aacute;ch gởi mail:&nbsp;<a href=\"mailto:htnhan@hcmus.edu.vn\">htnhan@hcmus.edu.vn</a>&nbsp;(sinh vi&ecirc;n phải ghi đầy đủ th&ocirc;ng tin v&agrave; c&oacute; nội dung r&otilde; r&agrave;ng) hoặc điện thoại số: 0919786248.</p>\r\n\r\n<p>Thời kh&oacute;a biểu c&aacute;c lớp ch&iacute;nh quy v&agrave; trả nợ sẽ được cập nhật th&ocirc;ng b&aacute;o tr&ecirc;n trang web khoa v&agrave; tr&ecirc;n hệ thống Portal.</p>\r\n\r\n<p>Gi&aacute;o vụ khoa Vật l&yacute; - Vật l&yacute; kỹ thuật.</p>\r\n\r\n<p>Ng&agrave;y 07/08/2017</p>\r\n', 'Thông báo đăng ký học phần HK I năm học 2017-2018\r\n\r\n'),
(206, 114, 2, 'Notice of registration for the HK I module for the 2017-2018 academic year', '<p>Students see the notice of registration for the first semester of the 2017-2018 school year on the school website. Registration time is from 21-27 August 2017. Note: Students register for courses in the main semester and pay off the loan during this official registration period (There is no separate repayment schedule). Students who need support and advice can contact the faculty department (Thay Nhan) by sending mail to: htnhan@hcmus.edu.vn (students must write full information and clear content) or phone number: 0919786248.</p>\r\n\r\n<p>The schedule of regular classes and repayment will be updated on the faculty website and on the Portal system.</p>\r\n\r\n<p>Department of Physics - Technical Physics.</p>\r\n\r\n<p>August 7, 2017</p>\r\n', 'Notice of registration for the HK I module for the 2017-2018 academic year'),
(207, 115, 1, 'Nhiều cơ hội việc làm dành cho sinh viên ngành Vật Lý', '<p>Một số cơ hội việc l&agrave;m d&agrave;nh cho sinh vi&ecirc;n ng&agrave;nh Vật L&yacute;:<br />\r\n-Olympus tuyển dụng kỹ sư:<br />\r\n<a href=\"http://www.phys.hcmus.edu.vn/vi/index.php?option=com_content&amp;view=article&amp;id=850:tuy-n-d-ng-k-su-cho-nhi-u-v-tri-t-i-cong-ty-olympus&amp;catid=20:tin-tuyn-dng&amp;Itemid=234\">http://www.phys.hcmus.edu.vn/vi/index.php?option=com_content&amp;view=article&amp;id=850:tuy-n-d-ng-k-su-cho-nhi-u-v-tri-t-i-cong-ty-olympus&amp;catid=20:tin-tuyn-dng&amp;Itemid=234</a><br />\r\n-GES tuyển kỹ sư quang học:<br />\r\n<a href=\"https://www.hcmus.edu.vn/component/content/article/129-ph%C3%B2ng-ctsv/thong-bao-viec-lam-va-thuc-tap/888-cong-ty-ges-vietnam-tuyen-dung-sv-tot-nghiep-khoa-vat-ly-vat-ly-ky-thuat?Itemid=437\">https://www.hcmus.edu.vn/component/content/article/129-ph&ograve;ng-ctsv/thong-bao-viec-lam-va-thuc-tap/888-cong-ty-ges-vietnam-tuyen-dung-sv-tot-nghiep-khoa-vat-ly-vat-ly-ky-thuat?Itemid=437</a><br />\r\n-Chương tr&igrave;nh thực tập m&ugrave;a h&egrave; tại c&ocirc;ng ty Ampere Computing :<br />\r\n<a href=\"https://www.vietnamworks.com/summer-internship-job-openings-in-ampere-computing-17-positions-947091-jd\">https://www.vietnamworks.com/summer-internship-job-openings-in-ampere-computing-17-positions-947091-jd</a><br />\r\n-Wellspring International Bilingual School Saigon tuyển gi&aacute;o vi&ecirc;n L&yacute;/H&oacute;a/Khoa học:<br />\r\n<a href=\"https://www.vietnamworks.com/physics-science-chemistry-teacher-native-speaker-1-945725-jd\">https://www.vietnamworks.com/physics-science-chemistry-teacher-native-speaker-1-945725-jd</a><br />\r\n-ARRIVE TECHNOLOGIES tuyển dụng kỹ sư phần mềm:</p>\r\n\r\n<p><a href=\"https://www.vietnamworks.com/senior-software-engineers-with-3-6-years-of-experience-938489-jd\">https://www.vietnamworks.com/senior-software-engineers-with-3-6-years-of-experience-938489-jd</a></p>\r\n\r\n<p>SCHAEFFLER tuyển dụng kỹ sư ph&ograve;ng th&iacute; nghiệm vật l&yacute;:<br />\r\n<a href=\"https://www.vietnamworks.com/physical-lab-engineer-1-945699-jd\">https://www.vietnamworks.com/physical-lab-engineer-1-945699-jd</a><br />\r\n-Renesas tuyển kỹ sư thiết kế vật l&yacute; c&oacute; kinh nghiệm:</p>\r\n\r\n<p><a href=\"https://www.vietnamworks.com/experienced-physical-design-engineer-8-positions-1-1-1-1-1-936941-jd\">https://www.vietnamworks.com/experienced-physical-design-engineer-8-positions-1-1-1-1-1-936941-jd</a></p>\r\n', 'Nhiều cơ hội việc làm dành cho sinh viên ngành Vật Lý'),
(208, 115, 2, 'Many job opportunities for students of Physics', '<p>Many job opportunities for students of Physics<br />\r\n-Olympus tuyển dụng kỹ sư:<br />\r\n<a href=\"http://www.phys.hcmus.edu.vn/vi/index.php?option=com_content&amp;view=article&amp;id=850:tuy-n-d-ng-k-su-cho-nhi-u-v-tri-t-i-cong-ty-olympus&amp;catid=20:tin-tuyn-dng&amp;Itemid=234\">http://www.phys.hcmus.edu.vn/vi/index.php?option=com_content&amp;view=article&amp;id=850:tuy-n-d-ng-k-su-cho-nhi-u-v-tri-t-i-cong-ty-olympus&amp;catid=20:tin-tuyn-dng&amp;Itemid=234</a><br />\r\n-GES tuyển kỹ sư quang học:<br />\r\n<a href=\"https://www.hcmus.edu.vn/component/content/article/129-ph%C3%B2ng-ctsv/thong-bao-viec-lam-va-thuc-tap/888-cong-ty-ges-vietnam-tuyen-dung-sv-tot-nghiep-khoa-vat-ly-vat-ly-ky-thuat?Itemid=437\">https://www.hcmus.edu.vn/component/content/article/129-ph&ograve;ng-ctsv/thong-bao-viec-lam-va-thuc-tap/888-cong-ty-ges-vietnam-tuyen-dung-sv-tot-nghiep-khoa-vat-ly-vat-ly-ky-thuat?Itemid=437</a><br />\r\n-Chương tr&igrave;nh thực tập m&ugrave;a h&egrave; tại c&ocirc;ng ty Ampere Computing :<br />\r\n<a href=\"https://www.vietnamworks.com/summer-internship-job-openings-in-ampere-computing-17-positions-947091-jd\">https://www.vietnamworks.com/summer-internship-job-openings-in-ampere-computing-17-positions-947091-jd</a><br />\r\n-Wellspring International Bilingual School Saigon tuyển gi&aacute;o vi&ecirc;n L&yacute;/H&oacute;a/Khoa học:<br />\r\n<a href=\"https://www.vietnamworks.com/physics-science-chemistry-teacher-native-speaker-1-945725-jd\">https://www.vietnamworks.com/physics-science-chemistry-teacher-native-speaker-1-945725-jd</a><br />\r\n-ARRIVE TECHNOLOGIES tuyển dụng kỹ sư phần mềm:</p>\r\n\r\n<p><a href=\"https://www.vietnamworks.com/senior-software-engineers-with-3-6-years-of-experience-938489-jd\">https://www.vietnamworks.com/senior-software-engineers-with-3-6-years-of-experience-938489-jd</a></p>\r\n\r\n<p>SCHAEFFLER tuyển dụng kỹ sư ph&ograve;ng th&iacute; nghiệm vật l&yacute;:<br />\r\n<a href=\"https://www.vietnamworks.com/physical-lab-engineer-1-945699-jd\">https://www.vietnamworks.com/physical-lab-engineer-1-945699-jd</a><br />\r\n-Renesas tuyển kỹ sư thiết kế vật l&yacute; c&oacute; kinh nghiệm:</p>\r\n\r\n<p><a href=\"https://www.vietnamworks.com/experienced-physical-design-engineer-8-positions-1-1-1-1-1-936941-jd\">https://www.vietnamworks.com/experienced-physical-design-engineer-8-positions-1-1-1-1-1-936941-jd</a></p>\r\n', 'Many job opportunities for students of Physics'),
(209, 116, 1, 'Tuyển dụng cử nhân, thạc sỹ, tiến sĩ Vật lý', '<p>Tuyển dụng cử nh&acirc;n, thạc sỹ, tiến sĩ Vật l&yacute;:</p>\r\n\r\n<p>-<a href=\"http://tuyencongchuc.vn/thong-bao/truong-dai-hoc-su-pham-ky-thuat-tp-hcm-thong-bao-tuyen-dung/\">Th&ocirc;ng b&aacute;o tuyển dụng của đại học sư phạm kỹ thuật TP HCM</a></p>\r\n\r\n<p>-<a href=\"http://hcmup.edu.vn/index.php?option=com_content&amp;view=article&amp;id=24353&amp;lang=vi&amp;site=0\">Th&ocirc;ng b&aacute;o tuyển dụng của đại học sư phạm TP HCM</a></p>\r\n\r\n<p>-<a href=\"http://tuyencongchuc.vn/thong-bao/so-giao-duc-va-dao-tao-tp-ho-chi-minh-tuyen-dung-vien-chuc-nam-hoc-2018-2019/\">Th&ocirc;ng b&aacute;o tuyển dụng của Sở Gi&aacute;o Dục-Đ&agrave;o Tạo TP HCM</a></p>\r\n\r\n<p><br />\r\nTham khảo c&aacute;c chương tr&igrave;nh đ&agrave;o tạo sau đại học của Khoa Vật L&yacute;: <a href=\"http://www.phys.hcmus.edu.vn/vi/index.php?option=com_banners&amp;task=click&amp;id=1\">Link</a></p>\r\n', 'Tuyển dụng cử nhân, thạc sỹ, tiến sĩ Vật lý'),
(210, 116, 2, 'Recruitment bachelors, masters, and PhD in Physics', '<p>Recruitment bachelors, masters, and PhD in Physics</p>\r\n\r\n<p>-<a href=\"http://tuyencongchuc.vn/thong-bao/truong-dai-hoc-su-pham-ky-thuat-tp-hcm-thong-bao-tuyen-dung/\">Th&ocirc;ng b&aacute;o tuyển dụng của đại học sư phạm kỹ thuật TP HCM</a></p>\r\n\r\n<p>-<a href=\"http://hcmup.edu.vn/index.php?option=com_content&amp;view=article&amp;id=24353&amp;lang=vi&amp;site=0\">Th&ocirc;ng b&aacute;o tuyển dụng của đại học sư phạm TP HCM</a></p>\r\n\r\n<p>-<a href=\"http://tuyencongchuc.vn/thong-bao/so-giao-duc-va-dao-tao-tp-ho-chi-minh-tuyen-dung-vien-chuc-nam-hoc-2018-2019/\">Th&ocirc;ng b&aacute;o tuyển dụng của Sở Gi&aacute;o Dục-Đ&agrave;o Tạo TP HCM</a></p>\r\n\r\n<p><br />\r\nRefer to the postgraduate training programs of the Faculty of Physics: <a href=\"http://www.phys.hcmus.edu.vn/vi/index.php?option=com_banners&amp;task=click&amp;id=1\">Link</a></p>\r\n', 'Recruitment bachelors, masters, and PhD in Physics'),
(211, 117, 1, 'Trường THPT Đinh Thiện Lý tuyển dụng giáo viên vật lý', '<p>Trường THPT Đinh Thiện L&yacute; tuyển dụng gi&aacute;o vi&ecirc;n vật l&yacute;:<br />\r\nM&ocirc; tả C&ocirc;ng việc<br />\r\n&nbsp;Giảng dạy m&ocirc;n học theo chuy&ecirc;n m&ocirc;n đ&atilde; được đ&agrave;o tạo<br />\r\n&nbsp;L&agrave;m c&ocirc;ng t&aacute;c chủ nhiệm (Nếu được ph&acirc;n c&ocirc;ng)<br />\r\n&nbsp;Tham gia c&aacute;c hoạt động gi&aacute;o dục học sinh theo định hướng của nh&agrave; trường<br />\r\nY&ecirc;u Cầu C&ocirc;ng Việc<br />\r\n&nbsp;Tốt nghiệp Đại học sư phạm chuy&ecirc;n ng&agrave;nh trở l&ecirc;n (Hoặc Đại học chuy&ecirc;n ng&agrave;nh v&agrave; c&oacute; chứng chỉ sư phạm). Ri&ecirc;ng đối với gi&aacute;o vi&ecirc;n &Acirc;m nhạc: Tốt nghiệp Đại học chuy&ecirc;n ng&agrave;nh Thanh nhạc.<br />\r\n&nbsp;Kiến thức học thuật s&acirc;u rộng.<br />\r\n&nbsp;C&oacute; năng lực học tập v&agrave; đam m&ecirc; đối mới gi&aacute;o dục.<br />\r\n&nbsp;Ưu ti&ecirc;n th&agrave;nh thạo c&aacute;c ứng dụng CNTT trong dạy học<br />\r\n&nbsp;Ưu ti&ecirc;n Tiếng Anh đạt B1 trở l&ecirc;n<br />\r\nTh&ocirc;ng tin chi tiết: <a href=\"https://vieclam.tuoitre.vn/vi/tim-viec-lam/tphcm-giao-vien-mon-ly.35AEA597.html\">Link</a><br />\r\nXem th&ecirc;m c&aacute;c th&ocirc;ng b&aacute;o tuyển dụng kh&aacute;c &nbsp;<a href=\"https://www.facebook.com/VLH2015/posts/878629182300334?hc_location=ufi\">tại đ&acirc;y</a>..</p>\r\n', 'Trường THPT Đinh Thiện Lý tuyển dụng...'),
(212, 117, 2, 'Dinh Thien Ly High School recruits physics teachers', '<p>Dinh Thien Ly High School recruits physics teachers:<br />\r\nJob description<br />\r\nTeaching subject-based subjects that have been trained<br />\r\nDo the homeroom (if assigned)<br />\r\nParticipate in student-oriented educational activities of the school<br />\r\nJob requirements<br />\r\nGraduated from University of Pedagogy major or higher (Or University specialized and pedagogic certificates). For Music teachers: Graduated from University major in Vocal.<br />\r\nExtensive academic knowledge.<br />\r\nHaving academic ability and passion for new education.<br />\r\nPrioritizing proficiency in IT applications in teaching<br />\r\nPriority in English reaches B1 and above<br />\r\nDetails: <a href=\"https://vieclam.tuoitre.vn/vi/tim-viec-lam/tphcm-giao-vien-mon-ly.35AEA597.html\">Link</a><br />\r\nSee more job postings here ..</p>\r\n', 'Dinh Thien Ly High School recruits physics teachers'),
(213, 118, 1, 'Công ty pin mặt trời First Solar tuyển dụng nhiều vị trí', '<p>C&ocirc;ng ty pin mặt trời First Solar tuyển dụng nhiều vị tr&iacute;:<br />\r\nFirst Solar l&agrave; một tập đo&agrave;n của Mỹ, chuy&ecirc;n sản xuất về tấm m&ocirc; đun năng lượng mặt trời h&agrave;ng đầu tr&ecirc;n thế giới. First Solar ra đời năm 1999 khi c&aacute;c nh&agrave; s&aacute;ng lập quyết định cung cấp cho thị trường điện mặt trời để giảm thiểu nhu cầu sử dụng c&aacute;c nguồn t&agrave;i nguy&ecirc;n thi&ecirc;n nhi&ecirc;n v&agrave; cải thiện m&ocirc;i trường. Để đ&aacute;p ứng nhu cầu nh&acirc;n lực cho nh&agrave; m&aacute;y mới tại Việt Nam, ch&uacute;ng t&ocirc;i cần tuyển số lượng lớn c&aacute;c vị tr&iacute; như sau:<br />\r\nNh&acirc;n vi&ecirc;n vận h&agrave;nh sản xuất / Manufacturing Operator (Số lượng: 400). M&atilde; số: VT1<br />\r\nKỹ thuật vi&ecirc;n sản xuất / MFG Tech (Số lượng: 100). M&atilde; số: VT2<br />\r\nKỹ thuật vi&ecirc;n bảo tr&igrave; / Technician / Maintenance (Số lượng: 90). M&atilde; số: VT3<br />\r\nKỹ thuật vi&ecirc;n quy tr&igrave;nh sản xuất / Technician - Manufacturing Engineer (Số lượng: 40). M&atilde; số: VT4<br />\r\nNh&acirc;n vi&ecirc;n kho / Material Handler (Số lượng: 60). M&atilde; số: VT5<br />\r\nKỹ sư chất lượng / Engineer &ndash; Quality (Số lượng: 25). M&atilde; số: VT6<br />\r\nKỹ thuật vi&ecirc;n bảo tr&igrave; hạ tầng nh&agrave; m&aacute;y / Engineer &ndash; Facilities (Số lượng: 20). M&atilde; số: VT7<br />\r\nKỹ thuật vi&ecirc;n vận h&agrave;nh hệ thống xử l&yacute; nước thải &amp; t&aacute;i chế / Waste Water &amp; Recycling Technician (Số lượng: 30). M&atilde; số: VT8<br />\r\nTh&ocirc;ng tin chi tiết:<a href=\"http://www.firstsolar.com/lp/First%20Solar%20Vietnam%20Careers\">http://www.firstsolar.com/lp/First%20Solar%20Vietnam%20Careers</a></p>\r\n', 'Công ty pin mặt trời First Solar tuyển dụng nhiều vị trí'),
(214, 118, 2, 'Solar company First Solar employs a variety of positions', '<p>The First Solar solar cell company employs many positions:<br />\r\nFirst Solar is an American corporation, specializing in manufacturing the top solar module panels in the world. First Solar was born in 1999 when the founders decided to supply the solar power market to minimize the need to use natural resources and improve the environment. To meet the needs of manpower for the new factory in Vietnam, we need to recruit a large number of positions as follows:<br />\r\nProduction Operator (Quantity: 400). Code: VT1<br />\r\nProduction technician / MFG Tech (Quantity: 100). Code: VT2<br />\r\nTechnician / Maintenance / Maintenance (Number: 90). Code: VT3<br />\r\nProduction process technician / Technician - Manufacturing Engineer (Quantity: 40). Code: VT4<br />\r\nWarehouse worker / Material Handler (Quantity: 60). Code: VT5<br />\r\nQuality Engineer / Engineer - Quality (Quantity: 25). Code: VT6<br />\r\nInfrastructure maintenance technician / Engineer - Facilities (Quantity: 20). Code: VT7<br />\r\nTechnician operating waste water &amp; recycling / Waste Water &amp; Recycling Technician (Quantity: 30). Code: VT8<br />\r\nDetails:&nbsp;<a href=\"http://www.firstsolar.com/lp/First%20Solar%20Vietnam%20Careers\">http://www.firstsolar.com/lp/First%20Solar%20Vietnam%20Careers</a></p>\r\n', 'Solar company First Solar employs a variety of positions'),
(215, 119, 1, 'Danh sách sinh viên chuyên ngành khóa 2015 (Chính thức)', '<p>BCN Khoa Vật l&yacute; &ndash; VLKT th&ocirc;ng b&aacute;o đến sinh vi&ecirc;n kh&oacute;a 2015 (v&agrave; 2014B) kết quả x&eacute;t đăng k&yacute; v&agrave;o chuy&ecirc;n ng&agrave;nh (CH&Iacute;NH THỨC) như sau:</p>\r\n\r\n<p>- Căn cứ v&agrave;o nguyện vọng của sinh vi&ecirc;n, căn cứ điều kiện v&agrave;o chuy&ecirc;n ng&agrave;nh của c&aacute;c chuy&ecirc;n ng&agrave;nh, Khoa Vật L&yacute; - VLKT quyết định những sinh vi&ecirc;n được v&agrave;o c&aacute;c chuy&ecirc;n ng&agrave;nh theo c&aacute;c danh s&aacute;ch sau:</p>\r\n\r\n<p>+ Danh s&aacute;ch 38 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLDT_-_Khoa_2015(1).pdf\">Vật l&yacute; Điện tử</a>&nbsp;(Điều kiện: Điểm trung b&igrave;nh học tập 3 học kỳ đầu &ge; 5.0, điểm m&ocirc;n Điện tử cơ bản &gt; 5.0, số t&iacute;n chỉ nợ kh&ocirc;ng qu&aacute; 15TC)</p>\r\n\r\n<p>+ Danh s&aacute;ch 15 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLCR_-_Khoa_2015(1).pdf\">Vật l&yacute; Chất rắn</a>&nbsp; (Điều kiện: Kh&ocirc;ng)</p>\r\n\r\n<p>+ Danh s&aacute;ch 13 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLHN_-_Khoa_2015(1).pdf\">Vật l&yacute; Hạt nh&acirc;n</a>&nbsp;(Điều kiện: Điểm trung b&igrave;nh học tập 3 học kỳ đầu &ge; 5.0, điểm m&ocirc;n Vật l&yacute; hạt nh&acirc;n &gt; 5.0, số t&iacute;n chỉ nợ kh&ocirc;ng qu&aacute; 09TC)</p>\r\n\r\n<p>+ Danh s&aacute;ch 05 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLDC_-_Khoa_2015(1).pdf\">Vật l&yacute; Địa cầu</a><a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLDC_-_Khoa_2015.pdf\">&nbsp;</a>(Điều kiện: Kh&ocirc;ng)</p>\r\n\r\n<p>+ Danh s&aacute;ch 10 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLLT_-_Khoa_2015(1).pdf\">Vật l&yacute; L&yacute; thuyết</a>&nbsp;(Điều kiện: Kh&ocirc;ng)</p>\r\n\r\n<p>+ Danh s&aacute;ch 41 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLTH_-_Khoa_2015(1).pdf\">Vật l&yacute; Tin học</a>&nbsp;(Điều kiện: Điểm trung b&igrave;nh học tập 3 học kỳ đầu &ge; 6.50, điểm m&ocirc;n Phương ph&aacute;p t&iacute;nh &gt;5.0, số t&iacute;n chỉ nợ kh&ocirc;ng qu&aacute; 9TC (t&iacute;nh lu&ocirc;n học phần anh văn chưa học),&nbsp;điểm m&ocirc;n Điện tử cơ bản &gt;5.0,)</p>\r\n\r\n<p>+ Danh s&aacute;ch 43 SV v&agrave;o chuy&ecirc;n ng&agrave;nh&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_CHUYEN_NGANH_VLUD-_Khoa_2015(2).pdf\">Vật l&yacute; Ứng Dụng</a>&nbsp;(Điều kiện: Điểm trung b&igrave;nh học tập 3 học kỳ đầu &ge; 5.5, số t&iacute;n chỉ nợ kh&ocirc;ng qu&aacute; 12TC)</p>\r\n\r\n<p>- 5 Sinh vi&ecirc;n&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Danh%20sach%20chuyen%20nganh/Khoa%202015/DS_SV_khong_vao_CHUYEN_NGANH_VL_-_Khoa_2015.pdf\">chưa v&agrave;o chuy&ecirc;n ng&agrave;nh</a>&nbsp;phải học lại c&aacute;c học phần đại cương c&ograve;n nợ v&agrave; c&aacute;c học phần chung trong năm học 2017-2018. Đến th&aacute;ng 01-2018, khoa sẽ x&eacute;t v&agrave;o chuy&ecirc;n ng&agrave;nh đợt 2 (kh&oacute;a 2015B). Khi đ&oacute;, nếu SV vẫn c&ograve;n nợ nhiều học phần v&agrave; điểm trung b&igrave;nh học tập qu&aacute; thấp cũng sẽ kh&ocirc;ng được v&agrave;o chuy&ecirc;n ng&agrave;nh v&agrave; phải đợi đến th&aacute;ng 06-2018 đăng k&yacute; chuy&ecirc;n ng&agrave;nh c&ugrave;ng với kh&oacute;a 2016.</p>\r\n\r\n<p>- Sinh vi&ecirc;n c&ograve;n nợ (hoặc chưa học) c&aacute;c học phần Anh văn phải ho&agrave;n tất c&aacute;c học phần n&agrave;y hoặc nộp chứng chỉ tiếng anh đạt chuẩn đầu ra theo quy định trước học kỳ l&agrave;m kh&oacute;a luận tốt nghiệp (học kỳ 8).</p>\r\n\r\n<p>Sinh vi&ecirc;n c&oacute; khiếu nại hoặc cần giải đ&aacute;p vui l&ograve;ng li&ecirc;n hệ Thầy Nhẫn (Gi&aacute;o Vụ Khoa, Email:&nbsp;<a href=\"mailto:htnhan@hcmus.edu.vn\">htnhan@hcmus.edu.vn</a>, Điện thoại: 0919 786 248). Khoa sẽ kh&ocirc;ng giải quyết c&aacute;c trường hợp thay đổi chuy&ecirc;n ng&agrave;nh sau khi đ&atilde; c&oacute; quyết định ch&iacute;nh thức. SV xem thời kh&oacute;a biểu v&agrave; danh s&aacute;ch c&aacute;c học phần của HK I năm học 2017-2018 tr&ecirc;n trang web Khoa v&agrave; bảng th&ocirc;ng b&aacute;o của Khoa, Bộ m&ocirc;n. Sinh vi&ecirc;n xem lịch sinh hoạt đầu kh&oacute;a tr&ecirc;n trang web của bộ m&ocirc;n hoặc bảng th&ocirc;ng b&aacute;o của bộ m&ocirc;n.&nbsp;</p>\r\n\r\n<p>Danh s&aacute;ch cập nhật ng&agrave;y 15 th&aacute;ng 08 năm 2017.</p>\r\n\r\n<p>BCN Khoa Vật l&yacute; - VLKT</p>\r\n', 'Danh sách sinh viên chuyên ngành khóa 2015 (Chính thức)\r\n'),
(216, 119, 2, 'List of specialized students of course 2015 (Official)', '<p>The BCN Faculty of Physics - VLKT informs students of course 2015 (and 2014B) the results of consideration for registration into the major (OFFICIAL) as follows:</p>\r\n\r\n<p>- Based on the aspirations of students, based on the conditions for specialization of majors, the Department of Physics - VLKT decides students to enter the majors according to the following list:</p>\r\n\r\n<p>+ List of 38 students majoring in Electronic Physics (Condition: Average GPA of the first 3 semesters &ge; 5.0, score of Basic Electronics subject&gt; 5.0, the number of outstanding credits does not exceed 15 credits)</p>\r\n\r\n<p>+ List of 15 students majoring in Solid Physics (Condition: None)</p>\r\n\r\n<p>+ List of 13 students majoring in Nuclear Physics (Condition: Average GPA of the first 3 semesters &ge; 5.0, score of Nuclear Physics&gt; 5.0, the number of outstanding credits does not exceed 09 credits)</p>\r\n\r\n<p>+ List of 05 students majoring in Earth Physics (Conditions: None)</p>\r\n\r\n<p>+ List of 10 students majoring in Theoretical Physics (Conditions: None)</p>\r\n\r\n<p>+ List of 41 students majoring in Computer Physics (Condition: Average GPA of the first 3 semesters &ge; 6.50, Method score of 5.0&gt;, the number of outstanding credits does not exceed 9 credits (including English modules) not learned), basic Electronics scores&gt; 5.0,)</p>\r\n\r\n<p>+ List of 43 students entering into Applied Physics (Condition: Average GPA of the first 3 semesters &ge; 5.5, the number of outstanding credits not exceeding 12 credits)</p>\r\n\r\n<p>- 5 Students who are not majoring in the subject must repeat the outstanding general modules and the general modules for the academic year 2017-2018. By January 1, 2018, the Faculty will consider the second major (Course 2015B). At that time, if students still owe a lot of modules and the academic average is too low, they will not be able to enter the major and have to wait until June 2018 to register with the 2016 class.</p>\r\n\r\n<p>- Students who still owe (or have not yet studied) the English modules must complete these modules or submit English certificates that meet the required learning outcomes before the semester to complete the graduation thesis (Semester 8).</p>\r\n\r\n<p>Students who have complaints or need answers should contact Thay Nhan (Khoa Department, Email: htnhan@hcmus.edu.vn, Phone: 0919 786 248). Faculty will not handle cases of specialized changes after the official decision. Students view the timetable and list of modules of HK I for the academic year 2017-2018 on the Faculty website and the notice board of the Faculty, Department. Students can view the schedule of the course at the beginning of the course on the subject website or the notice board</p>\r\n\r\n<p>Updated list August 15, 2017.</p>\r\n\r\n<p>BCN Department of Physics - VLKT</p>\r\n', 'List of specialized students of course 2015 (Official)'),
(217, 120, 1, 'Thời khóa biểu HK I 2017-2018 và lịch đăng ký học phần (cập nhật ngày 21/08)', '<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">Khoa Vật l&yacute; - VLKT th&ocirc;ng b&aacute;o đến sinh vi&ecirc;n Ng&agrave;nh Vật l&yacute; học v&agrave; Ng&agrave;nh Kỹ thuật hạt nh&acirc;n, c&aacute;c kh&oacute;a 2015 trở về trước, thời gian&nbsp;<a href=\"http://web.hcmus.edu.vn/images/stories/phong_dao_tao/16-17/hk2/thong_bao_sv_vv_dang_ky_hp_hk1_2017_2018.pdf\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\">đăng k&yacute; học phần qua mạng</a>&nbsp;sẽ bắt đầu từ 08 giờ 00 s&aacute;ng ng&agrave;y 23/08/2017:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">- Thời gian cho sinh vi&ecirc;n đăng k&yacute;: từ&nbsp;<strong>08h00 ng&agrave;y 23<strong>/08/2017&nbsp;</strong><strong>đến 16h00 ng&agrave;y</strong>&nbsp;27<strong>/08/2017</strong></strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">- Sinh vi&ecirc;n đăng k&yacute; đ&uacute;ng c&aacute;c học phần thuộc chuy&ecirc;n ng&agrave;nh của m&igrave;nh học theo danh s&aacute;ch c&aacute;c học phần trong thời kh&oacute;a biểu. Sinh vi&ecirc;n phải xem kỹ thời kh&oacute;a biểu trước khi đăng k&yacute; để tr&aacute;nh tr&ugrave;ng giờ học giữa c&aacute;c m&ocirc;n chung v&agrave; c&aacute;c m&ocirc;n chuy&ecirc;n ng&agrave;nh. SV xem thời kh&oacute;a biểu của&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/Thoi%20khoa%20bieu/HK%20I%202017-2018/TKB_HK_I__2017-2018_Vat_ly_va_KTHN_cap_nhat_23-08.xlsx\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\"><span style=\"color:#2d93d2\">Khoa Vật l&yacute; - VLKT</span>&nbsp;v&agrave;&nbsp;<span style=\"color:#2d93d2\">Ng&agrave;nh Kỹ thuật hạt nh&acirc;n</span>&nbsp;tại đ&acirc;y</a>&nbsp;(Cập nhật ng&agrave;y&nbsp;<span style=\"font-size:14px\"><strong><span style=\"color:#0000cd\">23/08/2017</span></strong></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:16px\"><span style=\"color:#ff0000\"><strong>Lưu &yacute;: Thời kh&oacute;a biểu sẽ được cập nhật thường xuy&ecirc;n tr&ecirc;n web khoa v&agrave; trong hệ thống Portal</strong></span></span>.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">-<span style=\"font-size:16px\"><strong>&nbsp;<span style=\"color:#0000ff\"><span style=\"background-color:#afeeee\">Sinh vi&ecirc;n đăng k&yacute; c&aacute;c m&ocirc;n chung v&agrave; chuy&ecirc;n ng&agrave;nh c&ograve;n nợ trong thời gian đăng k&yacute; học phần ch&iacute;nh thức n&agrave;y (tất cả c&aacute;c m&ocirc;n c&oacute; trong thời kh&oacute;a biểu)</span></span></strong></span>.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">- Sinh vi&ecirc;n kh&oacute;a 2015 chưa v&agrave;o chuy&ecirc;n ng&agrave;nh c&oacute; thể đăng k&yacute; học c&aacute;c m&ocirc;n chung, m&ocirc;n cơ sở ng&agrave;nh c&oacute; trong thời kh&oacute;a biểu v&agrave; sắp xếp thời gian để học lại c&aacute;c học phần đại cương c&ograve;n nợ.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">- Đối với c&aacute;c m&ocirc;n đại cương (1,5 năm đầu), SV sẽ đăng k&yacute; học lại theo lịch&nbsp;<a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=11785&amp;Itemid=293\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\">đăng k&yacute; trả nợ của Ph&ograve;ng Đ&agrave;o tạo</a>&nbsp;(<span style=\"color:#0000ff\"><strong><span style=\"background-color:#ee82ee\">từ ng&agrave;y 25/08 đến 01/09/2017</span></strong></span>). Nếu SV đăng k&yacute; học lại c&ugrave;ng với c&aacute;c lớp ch&iacute;nh thức của kh&oacute;a 2017, SV n&ecirc;n chọn lớp v&agrave; đi học trước theo lịch của kh&oacute;a 2017 (bắt đầu học từ tuần 21/08/2017). Thời kh&oacute;a biểu c&aacute;c kh&oacute;a&nbsp;<a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=11745&amp;Itemid=293\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\">2016, 2017</a>&nbsp;sinh vi&ecirc;n xem tr&ecirc;n web trường (Ph&ograve;ng Đ&agrave;o Tạo).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">-&nbsp;<strong>SV c&ograve;n nợ học phần Thực tập Vật l&yacute; đại cương 2 đăng k&yacute; học bằng m&ocirc;n thay thế l&agrave; Thực h&agrave;nh vật l&yacute; cơ sở (PHY10002)</strong>.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">-&nbsp;<span style=\"color:#ff0000\"><em><strong>Sinh vi&ecirc;n nợ học phần Nhập m&ocirc;n lập tr&igrave;nh học thay thế bằng m&ocirc;n Tin học cơ sở (Xem&nbsp;</strong></em></span><em><strong><a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=11752&amp;Itemid=293\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\"><span style=\"color:#ff0000\">th&ocirc;ng b&aacute;o đăng k&yacute; tại đ&acirc;y</span></a></strong></em><span style=\"color:#ff0000\"><em><strong>&nbsp;(thời gian đăng k&yacute; từ 22-24/08/2017)</strong></em></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">-&nbsp;<span style=\"color:#ff0000\"><u><em><strong><span style=\"background-color:#ffff00\">Sinh vi&ecirc;n c&ograve;n nợ học phần Đại số B2 li&ecirc;n hệ thầy Nhẫn để đăng k&yacute; danh s&aacute;ch gởi l&ecirc;n Ph&ograve;ng Đ&agrave;o tạo xin mở lớp. Lưu &yacute;: Đ&acirc;y l&agrave; học kỳ cuối PĐT mở lớp cho SV học lại học phần n&agrave;y.</span></strong></em></u></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">- Sinh vi&ecirc;n khẩn trương v&agrave;o đăng k&yacute; theo đ&uacute;ng thời gian quy định. Sau khi đăng k&yacute; th&agrave;nh c&ocirc;ng SV n&ecirc;n xuất kết quả ra file v&agrave; lưu lại để kiểm tra đối chiếu khi cần thiết.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">Sinh vi&ecirc;n xem th&ocirc;ng b&aacute;o đăng k&yacute; học phần chi tiết tr&ecirc;n trang&nbsp;<a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=11683&amp;Itemid=293\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\"><span style=\"color:#2d93d2\">web trường</span></a>.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">-<span style=\"color:#ff0000\">&nbsp;<span style=\"font-size:16px\">Lịch học HK I sẽ bắt đầu học từ tuần 04/09/2017</span></span><span style=\"font-size:16px\">.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">Sinh vi&ecirc;n cần hỗ trợ v&agrave; giải đ&aacute;p trong thời gian đăng k&yacute; học phần (<span style=\"background-color:#00ffff\">SV phải đọc kỹ c&aacute;c nội dung tr&ecirc;n trước khi xin hỗ trợ)</span>&nbsp;c&oacute; thể li&ecirc;n hệ Gi&aacute;o vụ Khoa (Thầy Nhẫn) qua số điện thoại: 0919786248 hoặc Email:&nbsp;<a href=\"mailto:%20%3Cscript%20type=\'text/javascript\'%3E%20%3C!--%20var%20prefix%20=%20\'ma\'%20+%20\'il\'%20+%20\'to\';%20var%20path%20=%20\'hr\'%20+%20\'ef\'%20+%20\'=\';%20var%20addy14677%20=%20\'htnhan\'%20+%20\'@\';%20addy14677%20=%20addy14677%20+%20\'hcmus\'%20+%20\'.\'%20+%20\'edu\'%20+%20\'.\'%20+%20\'vn\';%20document.write(\'%3Ca%20\'%20+%20path%20+%20\'\\\'\'%20+%20prefix%20+%20\':\'%20+%20addy14677%20+%20\'\\\'%3E\');%20document.write(addy14677);%20document.write(\'%3C\\/a%3E\');%20//--%3E\\n%20%3C/script%3E%3Cscript%20type=\'text/javascript\'%3E%20%3C!--%20document.write(\'%3Cspan%20style=\\\'display:%20none;\\\'%3E\');%20//--%3E%20%3C/script%3EThis%20email%20address%20is%20being%20protected%20from%20spambots.%20You%20need%20JavaScript%20enabled%20to%20view%20it.%20%3Cscript%20type=\'text/javascript\'%3E%20%3C!--%20document.write(\'%3C/\');%20document.write(\'span%3E\');%20//--%3E%20%3C/script%3E\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\">&nbsp;</a><a href=\"mailto:htnhan@hcmus.edu.vn\" style=\"padding:0px; font-family:Arial, &quot;Arial Unicode MS&quot;, Helvetica, sans-serif; text-align:justify; text-decoration:underline; color:#2d93d2\">htnhan@hcmus.edu.vn</a>&nbsp;(Lưu &yacute;: Khi gởi email sinh vi&ecirc;n phải ghi đầy đủ c&aacute;c th&ocirc;ng tin chi tiết v&agrave; k&egrave;m theo số điện thoại li&ecirc;n lạc).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">&nbsp;Sau ng&agrave;y 27/08/2017, sinh vi&ecirc;n xin đăng k&yacute; bổ sung hoặc điều chỉnh phải li&ecirc;n hệ Gi&aacute;o vụ khoa để được hỗ trợ. Thời hạn điều chỉnh l&agrave; ng&agrave;y 31/08/2017</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">Tp. HCM ng&agrave;y 18 th&aacute;ng 08 năm 2017</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:13px\"><span style=\"color:#0e2f43\"><span style=\"font-family:Arial,&quot;Arial Unicode MS&quot;,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">Gi&aacute;o vụ Khoa Vật l&yacute; - VLKT</span></span></span></span></p>\r\n', 'Thời khóa biểu HK I 2017-2018 và lịch đăng ký học phần (cập nhật ngày 21/08)\r\n\r\n'),
(218, 120, 2, '2017 HK I Timetable 2017-2018 and schedule for enrollment of classes (updated on August 21)', '<p>The Faculty of Physics - VLKT informs students of the Department of Physics and Nuclear Engineering, the 2015 courses and earlier, the online registration time will start from 08:00 am on August 23, 2017 :</p>\r\n\r\n<p>- Time for students to register: from 08:00 on August 23, 2017 to 16:00 on August 27, 2017</p>\r\n\r\n<p>- Students register correctly the modules of their major according to the list of modules in the schedule. Students must carefully review the timetable before registration to avoid overlapping between general and specialized subjects. Students see the timetable of the Department of Physics - VLKT and Nuclear Engineering Department here (Updated on 23/08/2017</p>\r\n\r\n<p>Note: The schedule will be updated regularly on the faculty website and in the Portal system.</p>\r\n\r\n<p>- Students who register for general and specialized subjects are still in debt during the registration of this official module (all subjects are on the schedule)</p>\r\n\r\n<p>- Students of course 2015 have not entered the major can register to study general subjects, basic subjects in the schedule and arrange time to relearn the remaining general modules.</p>\r\n\r\n<p>- For general subjects (first 1.5 years), students will re-register according to the repayment registration schedule of the Training Department (from August 25 to September 1, 2017). If students re-enroll with the official 2017 courses, they should choose a class and go to school first according to the schedule of the 2017 course (starting from August 21, 2017). The timetable of courses for 2016 and 2017 is available on the website (Training Room).</p>\r\n\r\n<p>- Students still owe the General Practice Internship 2 module to register for an alternative course called Basic Physics Practice (PHY10002).</p>\r\n\r\n<p>- Students who owe the introductory course of introductory programming instead of Basic IT (See registration notice here (registration time is from 22-24 August, 2017)</p>\r\n\r\n<p>- Students who still owe the Algebra B2 module contact Mr. Nhan to register a list to send to the Training Department for opening classes. Note: This is the last semester of PĐT opening classes for students to repeat this module.</p>\r\n\r\n<p>- Students urgently register at the prescribed time. After successful registration, students should export the results to file and save them to check and compare as necessary.</p>\r\n\r\n<p>Students see the notice of detailed enrollment on the school website.</p>\r\n\r\n<p>- HK I schedule will start learning from September 4, 2017.</p>\r\n\r\n<p>Students who need support and answers during the session (Students must read the above contents carefully before applying for support) can contact the Faculty Department (Thay Nhan) via phone number: 0919786248 or Email: htnhan@hcmus.edu.vn (Note: When sending an email, students must include all the details and the contact phone number).</p>\r\n\r\n<p>&nbsp;After August 27, 2017, students applying for additional registration or modification must contact the Faculty Department for assistance. The deadline for adjustment is August 31, 2017</p>\r\n\r\n<p>Tp. HCM, August 18, 2017</p>\r\n\r\n<p>Department of Physics - VLKT</p>\r\n', '2017 HK I Timetable 2017-2018 and schedule for enrollment of classes (updated on August 21)'),
(219, 121, 1, 'Công ty Mitsuba thông báo tuyển dụng sinh viên Khoa Vật Lý', '<p>C&ocirc;ng ty Mitsuba th&ocirc;ng b&aacute;o tuyển dụng sinh vi&ecirc;n Khoa Vật L&yacute;:&nbsp;<br />\r\nSinh vi&ecirc;n nộp hồ sơ trước 14/7. Sinh vi&ecirc;n năm cuối c&oacute; thể ứng tuyển với điều kiện kh&ocirc;ng nợ m&ocirc;n v&agrave; đảm bảo tốt nghiệp v&agrave;o th&aacute;ng 10.<br />\r\nTh&ocirc;ng tin chi tiết:&nbsp;<a href=\"https://drive.google.com/open?id=0B0yL-xLCx0YfMklaUUFKLVZMbFFWVzYxVTdIM2JOWDFXLTg4\">https://drive.google.com/open?id=0B0yL-xLCx0YfMklaUUFKLVZMbFFWVzYxVTdIM2JOWDFXLTg4</a></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/tuyendungMitsuba7-2018-1.jpg\" style=\"height:971px; width:750px\" /></p>\r\n', 'Công ty Mitsuba thông báo tuyển dụng...'),
(220, 121, 2, 'Mitsuba Company announces recruitment of students of the Department of Physics', '<p>Mitsuba Company announces recruitment of students of the Department of Physics:<br />\r\nStudents apply before 14/7. Final year students can apply for a condition of no debt and ensure graduation in October.<br />\r\nDetails:&nbsp;<a href=\"https://drive.google.com/open?id=0B0yL-xLCx0YfMklaUUFKLVZMbFFWVzYxVTdIM2JOWDFXLTg4\">https://drive.google.com/open?id=0B0yL-xLCx0YfMklaUUFKLVZMbFFWVzYxVTdIM2JOWDFXLTg4</a></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/khoa-vat-ly/tuyendungMitsuba7-2018-1.jpg\" style=\"height:971px; width:750px\" /></p>\r\n', 'Mitsuba Company announces recruitment of students of the Department of Physics'),
(221, 122, 1, 'Tập đoàn Bosch thông báo tuyển dụng', '<p>Nhu cầu tuyển dụng của tập đo&agrave;n Bosch:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/1Xo0Bc3007TQFvqPOI-fNCv02d4QVLKsr/view?usp=sharing\">https://drive.google.com/file/d/1Xo0Bc3007TQFvqPOI-fNCv02d4QVLKsr/view?usp=sharing</a></p>\r\n', 'Tập đoàn Bosch thông báo tuyển dụng'),
(222, 122, 2, 'The Bosch Group announces recruitment', '<p>Recruitment needs of Bosch Group:</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/1Xo0Bc3007TQFvqPOI-fNCv02d4QVLKsr/view?usp=sharing\">https://drive.google.com/file/d/1Xo0Bc3007TQFvqPOI-fNCv02d4QVLKsr/view?usp=sharing</a></p>\r\n', 'The Bosch Group announces recruitment');
INSERT INTO `posts_lang` (`id`, `post_id`, `lang_id`, `title`, `content`, `excerpt`) VALUES
(223, 123, 1, 'Thông báo số 1 V/v Xét tuyển hệ Cử nhân tài năng khóa 2017', '<p>TRƯỜNG ĐH KHTN TP. HCM&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CỘNG H&Ograve;A X&Atilde; HỘI CHỦ NGHĨA VIỆT NAM</p>\r\n\r\n<p>&nbsp; KHOA VẬT L&Yacute; &ndash; VẬT L&Yacute; KỸ THUẬT&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Độc lập &ndash; Tự do- Hạnh ph&uacute;c</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ----------------------------------</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Tp. HCM, ng&agrave;y 18 th&aacute;ng 08 năm 2017</em></p>\r\n\r\n<p><strong>Th&ocirc;ng b&aacute;o số 1</strong></p>\r\n\r\n<p><strong>V/V X&Eacute;T TUYỂN SINH VI&Ecirc;N V&Agrave;O HỆ CỬ NH&Acirc;N T&Agrave;I NĂNG</strong></p>\r\n\r\n<p><strong>NG&Agrave;NH VẬT L&Yacute; HỌC KH&Oacute;A 2017</strong></p>\r\n\r\n<p><strong>NĂM HỌC 2017-2018</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Được sự ph&ecirc; duyệt đề &aacute;n &ldquo;Đ&agrave;o tạo Cử nh&acirc;n t&agrave;i năng Ng&agrave;nh Vật l&yacute; học&rdquo;, của Trường ĐH Khoa học Tự nhi&ecirc;n Tp. HCM, nay BCN Khoa Vật l&yacute; &ndash; VLKT th&ocirc;ng b&aacute;o đến sinh vi&ecirc;n tr&uacute;ng tuyển v&agrave;o ng&agrave;nh Vật l&yacute; học - Trường ĐH Khoa học Tự nhi&ecirc;n kh&oacute;a tuyển 2017 như sau:</p>\r\n\r\n<p>&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Năm học 2017-2018, Khoa Vật l&yacute; &ndash; VLKT&nbsp;<strong>x&eacute;t tuyển 30 sinh vi&ecirc;n v&agrave;o học hệ Cử nh&acirc;n T&agrave;i năng Ng&agrave;nh Vật l&yacute; học kh&oacute;a tuyển 2017</strong></p>\r\n\r\n<p>&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Đối tượng x&eacute;t tuyển</strong></p>\r\n\r\n<p>o&nbsp;&nbsp; Tuyển thẳng những sinh vi&ecirc;n đạt c&aacute;c giải trong c&aacute;c kỳ thi Học sinh Giỏi Quốc gia, kỳ thi Olimpic Vật l&yacute; Quốc tế,&hellip;</p>\r\n\r\n<p>o&nbsp;&nbsp; Sinh vi&ecirc;n c&oacute;&nbsp;<strong>điểm thi 03 m&ocirc;n x&eacute;t tuyển trong kỳ thi TSĐH năm 2017&nbsp;</strong><strong>&sup3;</strong><strong>23,5 điểm</strong>(đ&atilde; cộng điểm ưu ti&ecirc;n)</p>\r\n\r\n<p>&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Hồ sơ x&eacute;t tuyển</strong></p>\r\n\r\n<p>o&nbsp;&nbsp; Bản sao giấy chứng nhận đạt c&aacute;c giải trong c&aacute;c kỳ thi Học sinh Giỏi Quốc gia, kỳ thi Olimpic Vật l&yacute; Quốc tế,&hellip;(nếu c&oacute;)</p>\r\n\r\n<p>o&nbsp;&nbsp; Đơn xin x&eacute;t tuyển (<a href=\"http://www.phys.hcmus.edu.vn/vi/images/stories/doc/2013/giaovu/He%20CNTN%202017/Don_xet_tuyen_he_CNTN_2017.doc\">theo mẫu</a>)</p>\r\n\r\n<p>o&nbsp;&nbsp; Hồ sơ gửi trực tiếp về VP. Khoa Vật l&yacute;-VLKT, Trường ĐH Khoa học Tự nhi&ecirc;n, D&atilde;y A, Ph&ograve;ng A01, 227 Nguyễn Văn Cừ, Q.5</p>\r\n\r\n<p>o&nbsp;&nbsp; Hoặc gửi qua email cho thầy Huỳnh Thanh Nhẫn:&nbsp;<a href=\"mailto:htnhan@hcmus.edu.vn\">htnhan@hcmus.edu.vn</a></p>\r\n\r\n<p>&middot;<strong>Thời gian x&eacute;t tuyển:</strong></p>\r\n\r\n<p>o&nbsp;&nbsp; Từ 18/08/2017 đến 01/09/2017</p>\r\n\r\n<p>o&nbsp;&nbsp; Ng&agrave;y 05/09/2017 c&ocirc;ng bố danh s&aacute;ch tr&uacute;ng tuyển tr&ecirc;n trang web khoa Vật l&yacute; &ndash; VLKT:&nbsp;<a href=\"http://www.phys.hcmus.edu.vn/\">www.phys.hcmus.edu.vn</a></p>\r\n\r\n<p>Sinh vi&ecirc;n cần th&ecirc;m th&ocirc;ng tin chi tiết c&oacute; thể li&ecirc;n hệ Gi&aacute;o vụ khoa - thầy Huỳnh Thanh Nhẫn qua số điện thoại: 0919786248, hoặc email:&nbsp;<a href=\"mailto:htnhan@hcmus.edu.vn\">htnhan@hcmus.edu.vn</a>.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trưởng Khoa Vật l&yacute; &ndash; Vật l&yacute; kỹ thuật</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; L&ecirc; Vũ Tuấn H&ugrave;ng</p>\r\n', 'Thông báo số 1 V/v Xét tuyển hệ Cử nhân tài năng khóa 2017\r\n'),
(224, 123, 2, 'Announcement No. 1 V / v Recruitment of the 2017 Bachelor of Talent', '<p style=\"text-align:center\">HCMC University of Science HCM SOCIALIST REPUBLIC OF VIETNAM</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;&nbsp;PHYSICAL SCIENCE - TECHNICAL PHYSICS Independence - Freedom - Happiness</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;----------------------------------</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tp. HCM, August 18, 2017</p>\r\n\r\n<p>Notice number 1</p>\r\n\r\n<p>V / V CONSIDERING STUDENTS TO RECEIVE TALENTED BATTERIES</p>\r\n\r\n<p>COURSE PHYSICS 2017</p>\r\n\r\n<p>SCHOOL YEAR 2017-2018</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Approved the project &quot;Training Bachelor of Talent in Physics&quot;, of Ho Chi Minh City University of Natural Sciences. HCM, now BCN Faculty of Physics - VLKT notify students to be admitted to Physics - University of Natural Sciences, the course of 2017 admission as follows:</p>\r\n\r\n<p>&middot; Academic year 2017-2018, Department of Physics - VLKT recruited 30 students to study the Bachelor of Talent in Physics 2017</p>\r\n\r\n<p>&middot; Subjects of admission</p>\r\n\r\n<p>o Directly recruit students who win prizes in the National Excellent Student exams, the International Olimpic Physics exam, ...</p>\r\n\r\n<p>o Students with 3 points in the 2017 TSR exam &sup3;23.5 (plus priority points)</p>\r\n\r\n<p>&middot; Application documents</p>\r\n\r\n<p>o Copies of the certificate of winning the awards in the National Excellent Student exams, Olimpic Physics International exam, ... (if any)</p>\r\n\r\n<p>o Application for admission (form)</p>\r\n\r\n<p>o Documents sent directly to VP. Department of Physics-VLKT, University of Natural Sciences, Series A, Room A01, 227 Nguyen Van Cu, District 5</p>\r\n\r\n<p>o Or email to Mr. Huynh Thanh Nhan: htnhan@hcmus.edu.vn<br />\r\n&middot; Admission time:</p>\r\n\r\n<p>o From August 18, 2017 to September 1, 2017</p>\r\n\r\n<p>o September 5, 2017 announced the successful list on the website of Department of Physics - VLKT: www.phys.hcmus.edu.vn</p>\r\n\r\n<p>Students who need more information can contact the Faculty Department - Mr. Huynh Thanh Nhan via phone number: 0919786248, or email: htnhan@hcmus.edu.vn.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Head of Department of Physics - Technical Physics</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Le Vu Tuan Hung</p>\r\n', 'Announcement No. 1 V / v Recruitment of the 2017 Bachelor of Talent'),
(225, 124, 1, 'Công ty New-Ocean thông báo tuyển dụng sinh viên Khoa Vật Lý', '<p>C&ocirc;ng ty New-Ocean th&ocirc;ng b&aacute;o tuyển dụng sinh vi&ecirc;n Khoa Vật L&yacute;</p>\r\n\r\n<p>Do nhu cầu c&ocirc;ng việc, hiện c&ocirc;ng ty ch&uacute;ng t&ocirc;i đang cần tuyển 2-3 Kỹ sư Vật l&yacute;/ Quang học như sau:</p>\r\n\r\n<p>&nbsp;- Đối ứng thiết kế, lắp đặt v&agrave; cải tiến c&ocirc;ng đoạn sản xuất sản phẩm mới,</p>\r\n\r\n<p>- T&igrave;m lỗi v&agrave; đưa ra giải ph&aacute;p khắc phục lỗi sản phẩm,</p>\r\n\r\n<p>&nbsp;- Thực hiện c&ocirc;ng việc được cấp tr&ecirc;n giao ph&oacute; li&ecirc;n quan đến nghiệp vụ nghi&ecirc;n cứu ( &aacute;nh s&aacute;ng, xử l&yacute; ảnh)</p>\r\n\r\n<p>- Những c&ocirc;ng việc c&oacute; li&ecirc;n quan kh&aacute;c.</p>\r\n\r\n<p>&nbsp;- Chi tiết về c&ocirc;ng việc sẽ được trao đổi cụ thể trong buổi phỏng vấn.</p>\r\n\r\n<p>&nbsp;&nbsp;Y&ecirc;u cầu:</p>\r\n\r\n<p>&nbsp;- Tốt nghiệp Cao Đẳng/ Đại học chuy&ecirc;n ngh&agrave;nh Vật l&yacute; hoặc Quang học ( Welcome tất cả c&aacute;c bạn sinh vi&ecirc;n mới ra trường, chưa c&oacute; kinh nghiệm)</p>\r\n\r\n<p>&nbsp;- Ưu ti&ecirc;n những ứng vi&ecirc;n biết sử dụng program thiết kế như (Auto cad (th&agrave;nh thạo), PRO-E (tr&igrave;nh độ sơ cấp trở l&ecirc;n)).</p>\r\n\r\n<p>&nbsp;- Ưu ti&ecirc;n những ứng vi&ecirc;n hiểu về bản vẽ kỹ thuật.</p>\r\n\r\n<p>&nbsp;- C&oacute; tinh thần học hỏi</p>\r\n\r\n<p>C&aacute;c ứng vi&ecirc;n quan t&acirc;m vui l&ograve;ng gửi hồ sơ qua email:&nbsp;&nbsp; &nbsp;<br />\r\n<a href=\"mailto:hr@new-ocean.com.vn\">hr@new-ocean.com.vn</a> (Ms. Linh)</p>\r\n\r\n<p>Hạn nộp hồ sơ: tuyển li&ecirc;n tục cho đến khi đủ số lượng ứng vi&ecirc;n<br />\r\n&nbsp;</p>\r\n', 'Công ty New-Ocean thông báo tuyển dụng...'),
(226, 124, 2, 'New-Ocean Company announces recruitment of students of the Department of Physics', '<p>New-Ocean Company announces recruitment of students of the Department of Physics</p>\r\n\r\n<p>Due to work demands, our company is currently recruiting 2-3 Physics / Optical Engineers as follows:</p>\r\n\r\n<p>- Reciprocal design, installation and improvement of manufacturing stages of new products,</p>\r\n\r\n<p>- Find errors and offer solutions to fix product errors,</p>\r\n\r\n<p>- Perform tasks assigned by their superiors related to research operations (lighting, image processing)</p>\r\n\r\n<p>- Other related work.</p>\r\n\r\n<p>- Details of the job will be discussed in the interview.</p>\r\n\r\n<p>Request:</p>\r\n\r\n<p>- Graduated College / University majoring in Physics or Optics (Welcome all newly graduated students, not yet experienced)</p>\r\n\r\n<p>- Priority to candidates who know how to use design programs such as (Auto cad (proficient), PRO-E (elementary level or higher)).</p>\r\n\r\n<p>- Prioritize candidates who understand technical drawings.</p>\r\n\r\n<p>- With the spirit of learning</p>\r\n\r\n<p>Interested candidates please email your resume:<br />\r\n<a href=\"mailto:hr@new-ocean.com.vn\">hr@new-ocean.com.vn</a> (Ms. Linh)</p>\r\n\r\n<p>Application deadlines: continuous recruitment until sufficient number of candidates</p>\r\n', 'New-Ocean Company announces recruitment of students of the Department of Physics'),
(227, 125, 1, 'Thông báo V/v đăng ký học lại môn Đại số B2', '<p>Hiện nay c&ograve;n một số Sv kh&oacute;a 2015 trở về trước c&ograve;n nợ m&ocirc;n Đại số B2 chưa c&oacute; lớp học trả nợ do chương tr&igrave;nh đổi mới từ kh&oacute;a 2016 kh&ocirc;ng c&ograve;n m&ocirc;n học n&agrave;y v&agrave; cũng kh&ocirc;ng c&oacute; m&ocirc;n tương đương thay thế. Để gi&uacute;p cho sinh vi&ecirc;n c&oacute; thể đăng k&yacute; học trả nợ m&ocirc;n n&agrave;y, GV khoa đ&atilde; xin ph&eacute;p Ph&ograve;ng Đ&agrave;o tạo cho sinh vi&ecirc;n lập danh s&aacute;ch đăng k&yacute; để ph&ograve;ng ĐT căn cứ v&agrave;o danh s&aacute;ch mở lớp trả nợ (lần cuối). Vậy sinh vi&ecirc;n n&agrave;o c&ograve;n nợ m&ocirc;n học n&agrave;y phải đăng k&yacute; học trong đợt n&agrave;y. SV gởi th&ocirc;ng tin (họ v&agrave; t&ecirc;n, MSSV) qua email cho thầy Nhẫn trước ng&agrave;y 04/09/2017. Thầy Nhẫn sẽ lập danh s&aacute;ch v&agrave; tr&igrave;nh l&ecirc;n PĐT xin mở lớp trong ng&agrave;y 05/09/2017. Khoa sẽ kh&ocirc;ng giải quyết c&aacute;c trường hợp đăng k&yacute; trễ hạn. Lịch học cụ thể sẽ do PĐT th&ocirc;ng b&aacute;o. Sinh vi&ecirc;n phải thường xuyễn xem th&ocirc;ng tin cập nhật tr&ecirc;n web khoa, web trường.</p>\r\n\r\n<p>Tp. HCM ng&agrave;y 25 th&aacute;ng 08 năm 2017</p>\r\n\r\n<p>GV Khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo V/v đăng ký học lại môn Đại số B2\r\n'),
(228, 125, 2, 'Notice of re-enrollment of Algebra B2 subject', '<p>At present, there are still some students in the 2015 class and back, who still owe the Algebra B2 course without a repayment class because the 2016 renovation program has no subjects and no equivalent courses. In order to help students be able to register to pay for this subject, the faculty has asked the Training Department for permission to make a list of registered students so that the department will base on the list of classes to repay debt (the last time). So students who owe this subject must register for this session. Students send information (full name, MSSV) via email to Mr. Nhan before September 4, 2017. Mr. Rings will make a list and submit to PĐT for class opening on September 5, 2017. Faculty will not handle cases of late registration. Specific schedule will be notified by the PC. Students often have to see updated information on faculty websites and websites.</p>\r\n\r\n<p>Tp. HCM, August 25, 2017</p>\r\n\r\n<p>Faculty of Physics - VLKT</p>\r\n', 'Notice of re-enrollment of Algebra B2 subject'),
(229, 126, 1, 'Tuyển dụng 2 kỹ thuật viên Gamma Scan', '<p>C&ocirc;ng ty CP Bảo dưỡng v&agrave; sửa chữa C&ocirc;ng tr&igrave;nh Dầu kh&iacute; - Chi nh&aacute;nh Giải ph&aacute;p C&ocirc;ng nghệ Kiểm tra Kh&ocirc;ng ph&aacute; hủy Dầu kh&iacute; Việt Nam [Thanh H&oacute;a]<br />\r\nDo nhu cầu ph&aacute;t triển nguồn lực kỹ thuật vi&ecirc;n chất lượng cao, ch&uacute;ng t&ocirc;i cần tuyển 2 kỹ thuật vi&ecirc;n Gamma Scan l&agrave;m việc tại Thanh H&oacute;a với y&ecirc;u cầu như sau:&nbsp;<br />\r\n-Nam tuổi dưới 35 tuổi, Tốt nghiệp Đại học c&aacute;c chuy&ecirc;n ng&agrave;nh Vật L&yacute; Hạt Nh&acirc;n, Vật L&yacute; Chất Rắn, Vật l&yacute; Ứng Dụng, Cơ kh&iacute; Chế Tạo M&aacute;y hoặc c&aacute;c ng&agrave;nh tương tự.&nbsp;<br />\r\n-Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; chứng chỉ kiểm tra kh&ocirc;ng ph&aacute; hủy NDT&nbsp;<br />\r\n-Ưu ti&ecirc;n ứng vi&ecirc;n học c&aacute;c trường Đại Học B&aacute;ch Khoa H&agrave; Nội, Tp HCM, Đại học Khoa Học Tự Nhi&ecirc;n H&agrave; Nội, Tp HCM, Đại Học Sư Phạm kỹ Thuật TP HCM&nbsp;<br />\r\n-Tiếng Anh: chứng chỉ B hoặc tương đương&nbsp;<br />\r\n-Tin Học: Tối thiểu Chứng chỉ B tin học&nbsp;<br />\r\n-Tư duy Logic, kỹ năng xử l&yacute; t&igrave;nh huống<br />\r\nTh&ocirc;ng tin chi tiết: <a href=\"https://drive.google.com/file/d/0B0yL-xLCx0YfZkptTjVBN3BkcHIyaDZNR2ZfSXdic0luRF9J/view\">https://drive.google.com/file/d/0B0yL-xLCx0YfZkptTjVBN3BkcHIyaDZNR2ZfSXdic0luRF9J/view</a></p>\r\n', 'Tuyển dụng 2 kỹ thuật viên Gamma Scan'),
(230, 126, 2, 'Recruiting 2 Gamma Scan technicians', '<p>PetroVietnam Works Maintenance and Repair Joint Stock Company - Vietnam Petroleum Non Destructive Testing Technology Solution Branch [Thanh Hoa]<br />\r\nDue to the need to develop high-quality technician resources, we need to recruit 2 Gamma Scan technicians to work in Thanh Hoa with the following requirements:<br />\r\n- Male under 35 years old, University graduate with specializations in Nuclear Physics, Solid Physics, Applied Physics, Mechanical Engineering or similar industries.<br />\r\n-First, candidates with NDT non-destructive testing certificate<br />\r\n-Priority is given to candidates who study at Hanoi University of Technology, Ho Chi Minh City, Hanoi University of Science, Ho Chi Minh City, HCMC University of Technical Education<br />\r\n-English: certificate B or equivalent<br />\r\n- Study: Minimum Certificate B in informatics<br />\r\n-Relative thinking, problem solving skills<br />\r\nDetails: <a href=\"https://drive.google.com/file/d/0B0yL-xLCx0YfZkptTjVBN3BkcHIyaDZNR2ZfSXdic0luRF9J/view\">https://drive.google.com/file/d/0B0yL-xLCx0YfZkptTjVBN3BkcHIyaDZNR2ZfSXdic0luRF9J/view</a></p>\r\n', 'Recruiting 2 Gamma Scan technicians'),
(231, 127, 1, 'Thông báo hủy lớp Điện tử cơ bản (VLH105)', '<p>Khoa Vật l&yacute; - Vật l&yacute; kỹ thuật th&ocirc;ng b&aacute;o: Do số sinh vi&ecirc;n đăng k&yacute; học M&ocirc;n điện tử cơ bản (VLH105) dưới 10 sinh vi&ecirc;n n&ecirc;n khoa sẽ hủy lớp học n&agrave;y. Sinh vi&ecirc;n chuyển sang đăng k&yacute; học m&ocirc;n kh&aacute;c v&agrave; sẽ đăng k&yacute; học lại m&ocirc;n Điện tử cơ bản trong học kỳ II năm học 2017-2018.</p>\r\n\r\n<p>Tp. HCM ng&agrave;y 28/08/2017</p>\r\n\r\n<p>GV Khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo hủy lớp Điện tử cơ bản (VLH105)'),
(232, 127, 2, 'Notice of Basic Electronics class cancellation (VLH105)', '<p>Department of Physics - Technical Physics informed: Because the number of students enrolled in Basic Electronic Science (VLH105) is less than 10 students, the department will cancel this class. Students transfer to another subject and will re-enroll in Basic Electronics in the second semester of the 2017-2018 school year.</p>\r\n\r\n<p>Tp. HCM, August 28, 2017</p>\r\n\r\n<p>Faculty of Physics - VLKT</p>\r\n', 'Notice of Basic Electronics class cancellation (VLH105)'),
(233, 128, 1, 'Tuyển 1 Cử nhân Vật lý Lý thuyết', '<p>Trung t&acirc;m INOMAR đang cần tuyển 1 cử nh&acirc;n chuy&ecirc;n ng&agrave;nh Vật l&yacute; l&yacute; thuyết, điểm tốt nghiệp tr&ecirc;n 7.5.<br />\r\nHồ sơ gồm CV, bảng điểm v&agrave; bằng cấp vui l&ograve;ng gửi đến PGS.TS Phan B&aacute;ch Thắng qua mail:<a href=\"mailto:pbthang@inomar.edu.vn\">pbthang@inomar.edu.vn</a>&nbsp;hoặc&nbsp;<a href=\"mailto:pbthang@skku.edu\">pbthang@skku.edu</a><br />\r\nT&igrave;m hiểu về c&aacute;c hướng nghi&ecirc;n cứu của trung t&acirc;m &nbsp;INOMAR: http://inomar.edu.vn</p>\r\n', 'Tuyển 1 Cử nhân Vật lý Lý thuyết'),
(234, 128, 2, 'Recruitment 1 Bachelor in Theoretical Physics', '<p>INOMAR center is looking for a bachelor degree in Theoretical Physics, graduation score above 7.5.<br />\r\nDocuments including CV, transcript and qualifications should be sent to Assoc.Prof. Dr. Phan Bach Thang via email:&nbsp;<a href=\"mailto:pbthang@inomar.edu.vn\">pbthang@inomar.edu.vn</a>&nbsp;or&nbsp;<a href=\"mailto:pbthang@skku.edu\">pbthang@skku.edu</a></p>\r\n\r\n<p>Learn about research directions of INOMAR center: http://inomar.edu.vn</p>\r\n', 'Recruitment 1 Bachelor in Theoretical Physics'),
(235, 129, 1, 'Thông báo V/v Cập nhật thời khóa biểu phòng học', '<p>Sinh vi&ecirc;n lưu &yacute; một số cập nhật thời kh&oacute;a biểu như sau:</p>\r\n\r\n<p>1. Sinh vi&ecirc;n đăng k&yacute; học lại Anh Văn 3, 4 ở Nguyễn Văn Cừ xem ph&ograve;ng học v&agrave; cập nhật danh s&aacute;ch c&aacute;c lớp tr&ecirc;n&nbsp;<a href=\"http://web.hcmus.edu.vn/index.php?option=com_content&amp;task=view&amp;id=11838&amp;Itemid=293\">trang web PĐT</a>.</p>\r\n\r\n<p>2. Từ tuần 11/09/2017 Sinh vi&ecirc;n học Vật l&yacute; nguy&ecirc;n tử sẽ chuyển ph&ograve;ng học từ B43 sang ph&ograve;ng E306 (ph&ograve;ng m&aacute;y lạnh).</p>\r\n\r\n<p>3.&nbsp;<strong>Sinh vi&ecirc;n học m&ocirc;n Vật l&yacute; chất rắn tạm thời chuyển sang học tại ph&ograve;ng C33 trong ng&agrave;y Thứ Hai 11/09/2017 l&yacute; do Giảng đường 1 bận tổ chức hội thảo</strong>.</p>\r\n\r\n<p>4.&nbsp;<strong>Sinh vi&ecirc;n Ng&agrave;nh Vật l&yacute; học v&agrave; Ng&agrave;nh Kỹ thuật hạt nh&acirc;n học m&ocirc;n Thực h&agrave;nh vật l&yacute; cơ sở sẽ bắt đầu học từ tuần 18/09/2017.</strong></p>\r\n\r\n<p>Tp. HCM, ng&agrave;y 08/09/2017</p>\r\n\r\n<p>Gi&aacute;o vụ Khoa Vật l&yacute; - VLKT</p>\r\n', 'Thông báo V/v Cập nhật thời khóa biểu phòng học\r\n'),
(236, 129, 2, 'Notify V / v Update class schedule', '<p>Students note some updated schedule as follows:</p>\r\n\r\n<p>1. Students re-register to study English 3 and 4 at Nguyen Van Cu to view the classroom and update the list of classes on the PĐT website.</p>\r\n\r\n<p>2. From the 11th of September 2017, Students in Atomic Physics will move from B43 to E306 (air-conditioned room).</p>\r\n\r\n<p>3. Students studying Physics are temporarily transferred to study in room C33 on Monday 11/09/2017 the reason Lecture 1 was busy organizing workshops.</p>\r\n\r\n<p>4. Students in the Department of Physics and Nuclear Engineering Department of Basic Physics Practice will start studying on September 18, 2017.</p>\r\n\r\n<p>Tp. HCM, September 8, 2017</p>\r\n\r\n<p>Department of Physics - VLKT</p>\r\n', 'Notify V / v Update class schedule'),
(237, 130, 1, 'GES tuyển 5 kỹ sư quang học', '<p>C&ocirc;ng Ty TNHH Sản xuất Dịch vụ C&ocirc;ng nghệ B&aacute;n dẫn To&agrave;n Cầu (GES) tuyển 5 kỹ sư quang học<br />\r\nGES l&agrave; c&ocirc;ng ty c&oacute; 100% vốn đầu tư nước ngo&agrave;i, c&oacute; trụ sở tại Mỹ. GES hoạt động trong c&aacute;c lĩnh vực thiết kế, sản xuất, sửa chữa, n&acirc;ng cấp v&agrave; dịch vụ kh&aacute;ch h&agrave;ng về c&aacute;c thiết bị trong ng&agrave;nh c&ocirc;ng nghệ b&aacute;n dẫn. M&ocirc; h&igrave;nh kinh doanh của GES kết hợp rất nhiều trang thiết bị kỹ thuật hiện đại từ Hoa Kỳ v&agrave; Nhật Bản với t&agrave;i nguy&ecirc;n chi ph&iacute; thấp v&agrave; tiện nghi tại Việt Nam, một trong những m&ocirc;i trường năng động nhất ở Ch&acirc;u &Aacute;.<br />\r\nHiện tại c&ocirc;ng ty cần tuyển dụng vị tr&iacute; kỹ sư quang học, c&oacute; thể tuyển c&aacute;c bạn sinh vi&ecirc;n tốt nghiệp ng&agrave;nh Vật l&yacute;, y&ecirc;u cầu cụ thể trong file đ&iacute;nh k&egrave;m.</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/0B0yL-xLCx0YfNzFtVnpIOGxGVUhfQUtHUU1LNVFXaExkbnRz/view?usp=sharing\">https://drive.google.com/file/d/0B0yL-xLCx0YfNzFtVnpIOGxGVUhfQUtHUU1LNVFXaExkbnRz/view?usp=sharing</a></p>\r\n', 'GES tuyển 5 kỹ sư quang học'),
(238, 130, 2, 'GES recruited 5 optical engineers', '<p>Global Semiconductor Technology Services Manufacturing Co., Ltd. (GES) employs 5 optical engineers<br />\r\nGES is a company with 100% foreign capital, based in the US. GES operates in the areas of design, manufacture, repair, upgrade and customer service of semiconductor technology equipment. GES&#39;s business model combines a lot of modern technology equipment from the United States and Japan with low-cost resources and facilities in Vietnam, one of the most dynamic environments in Asia.<br />\r\nCurrently the company needs to recruit an optical engineer position, can recruit graduates in Physics, specific requirements in the attached file.</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/0B0yL-xLCx0YfNzFtVnpIOGxGVUhfQUtHUU1LNVFXaExkbnRz/view?usp=sharing\">https://drive.google.com/file/d/0B0yL-xLCx0YfNzFtVnpIOGxGVUhfQUtHUU1LNVFXaExkbnRz/view?usp=sharing</a></p>\r\n', 'GES recruited 5 optical engineers'),
(239, 131, 1, 'Thông báo mở lớp học lại Đại số B2 (TTH004), ĐHCQ', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>Th&ocirc;ng b&aacute;o mở lớp học lại Đại số B2 (TTH004), ĐHCQ</td>\r\n			<td><a href=\"http://web.hcmus.edu.vn/index2.php?option=com_content&amp;task=view&amp;id=11869&amp;pop=1&amp;page=0&amp;Itemid=293\" target=\"_blank\" title=\"In\"><img alt=\"In\" src=\"http://web.hcmus.edu.vn/images/M_images/printButton.png\" /></a></td>\r\n			<td><a href=\"http://web.hcmus.edu.vn/index2.php?option=com_content&amp;task=emailform&amp;id=11869&amp;itemid=293\" target=\"_blank\" title=\"E-mail\"><img alt=\"E-mail\" src=\"http://web.hcmus.edu.vn/images/M_images/emailButton.png\" /></a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"vertical-align:top\">\r\n			<p><strong>TH&Ocirc;NG B&Aacute;O</strong></p>\r\n\r\n			<p><strong>(Mở lớp học lại Đại số B2 - TTH004)</strong></p>\r\n\r\n			<p><strong>&nbsp;</strong></p>\r\n\r\n			<p><strong>SINH VI&Ecirc;N HỆ ĐẠI HỌC CH&Iacute;NH QUY NG&Agrave;NH VẬT L&Yacute;, KỸ THUẬT HẠT NH&Acirc;N, ĐIỆN TỬ KỸ THUẬT TRUYỀN TH&Ocirc;NG KH&Oacute;A 2015 TRỞ VỀ TRƯỚC VỀ THỜI KH&Oacute;A BIỂU HỌC LẠI M&Ocirc;N ĐẠI SỐ B2 NHƯ SAU:</strong></p>\r\n\r\n			<p>&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>HỌC V&Agrave;O THỨ 5, TIẾT 13-15 (Từ 17h50).</strong></p>\r\n\r\n			<p>&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>ĐỊA ĐIỂM HỌC: TẠI PH&Ograve;NG C.31 - cơ sở 227NVC.</strong></p>\r\n\r\n			<p>&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>BẮT ĐẦU HỌC: TỪ NG&Agrave;Y 14/9/2017</strong></p>\r\n\r\n			<p><strong>&nbsp;</strong></p>\r\n\r\n			<p><strong><em>Lưu &yacute;: đ&acirc;y l&agrave; lần mở cuối cho m&ocirc;n học n&agrave;y, c&aacute;c sinh vi&ecirc;n chưa đăng k&yacute; th&igrave; bổ sung danh s&aacute;ch cho Thầy Nhẫn Khoa Vật l&yacute; chậm nhất l&agrave; ng&agrave;y 20/9/2017.</em></strong></p>\r\n\r\n			<p><strong>&nbsp;</strong></p>\r\n\r\n			<