INSERT INTO `posts_lang` (`id`, `post_id`, `lang_id`, `title`, `content`, `excerpt`) VALUES
(415, 222, 1, 'Mời tham dự buổi bảo vệ luận án tiến sĩ của NCS Trần Hoàng Cao Sơn và Hồ Minh Trung', '<p>K&iacute;nh ch&agrave;o qu&yacute; thầy c&ocirc;,</p>\r\n\r\n<p>Khoa Vật l&yacute; &ndash; VLKT, Bộ m&ocirc;n Vật l&yacute; Ứng dụng sẽ tổ chức buổi bảo vệ luận &aacute;n tiến sĩ cấp cơ sở đ&agrave;o tạo cho hai nghi&ecirc;n cứu sinh Trần Ho&agrave;ng Cao Sơn v&agrave; Hồ Minh Trung theo lịch tr&igrave;nh v&agrave; địa điểm cụ thể như sau:</p>\r\n\r\n<p>1.Nghi&ecirc;n cứu sinh Trần Ho&agrave;ng Cao Sơn</p>\r\n\r\n<p>-Thời gian tổ chức: 9 h ng&agrave;y 26/12/2014</p>\r\n\r\n<p>-Địa điểm tổ chức: Ph&ograve;ng F102, Trường Đại học KHTN, 227 NVC, Quận 5</p>\r\n\r\n<p>-Đề t&agrave;i: Chế tạo v&agrave; nghi&ecirc;n cứu c&aacute;c cấu tr&uacute;c dị chất hữu cơ v&ocirc; cơ ứng dụng trong pin mặt trời hữu cơ</p>\r\n\r\n<p>2.Nghi&ecirc;n cứu sinh Hồ Minh Trung</p>\r\n\r\n<p>-Thời gian tổ chức: 9 h ng&agrave;y 31/12/2014</p>\r\n\r\n<p>-Địa điểm tổ chức: Ph&ograve;ng F102, Trường Đại học KHTN, 227 NVC, Quận 5</p>\r\n\r\n<p>-Đề t&agrave;i: Nghi&ecirc;n cứu t&iacute;nh chất quang của c&aacute;c hạt tinh thể nano InP chế tạo bằng phương ph&aacute;p ho&aacute; ướt v&agrave; ho&aacute; si&ecirc;u &acirc;m</p>\r\n\r\n<p>Khoa VL &ndash; VLKT tr&acirc;n trọng k&iacute;nh mời thầy c&ocirc;, sinh vi&ecirc;n v&agrave; c&aacute;c c&aacute; nh&acirc;n quan t&acirc;m tham dự buổi bảo vệ n&agrave;y.</p>\r\n\r\n<p>K&iacute;nh ch&agrave;o tr&acirc;n trọng.</p>\r\n', 'Mời tham dự buổi bảo vệ luận án tiến sĩ của NCS Trần Hoàng Cao Sơn và Hồ Minh Trung'),
(416, 222, 2, 'Invite to attend the doctoral thesis defense sessions of PhD students Tran Hoang Cao Son and Ho Minh Trung', '<p>Welcome teachers,</p>\r\n\r\n<p>The Faculty of Physics - VLKT, Department of Applied Physics will hold a doctoral thesis defense session for two graduate students Tran Hoang Cao Son and Ho Minh Trung according to the schedule and specific location as follows: :</p>\r\n\r\n<p>1.Research student Tran Hoang Cao Son</p>\r\n\r\n<p>-Time held: 9 h on 26/12/2014</p>\r\n\r\n<p>-Position location: Room F102, University of Natural Sciences, 227 NVC, District 5</p>\r\n\r\n<p>- Subject: Fabrication and research of inorganic organic matter structures applied in organic solar cells</p>\r\n\r\n<p>2. Ho Minh Trung research fellow</p>\r\n\r\n<p>-Time held: 9 hours on 12/31/2014</p>\r\n\r\n<p>-Position location: Room F102, University of Natural Sciences, 227 NVC, District 5</p>\r\n\r\n<p>- Subject: Research on optical properties of InP nanocrystals manufactured by wet chemical and ultrasonic chemistry</p>\r\n\r\n<p>The Faculty of VL - VLKT cordially invites teachers, students and interested individuals to attend this security event.</p>\r\n\r\n<p>Best regards<br />\r\n&nbsp;</p>\r\n', 'Invite to attend the doctoral thesis defense sessions of PhD students Tran Hoang Cao Son and Ho Minh Trung'),
(417, 223, 1, 'Mời tham dự seminar khoa học của Giáo sư Philippe Quentin', '<p>K&iacute;nh mời qu&yacute; thầy c&ocirc;/c&aacute;c bạn Nghi&ecirc;n Cứu Sinh/Học Vi&ecirc;n Cao Học/Sinh Vi&ecirc;n quan t&acirc;m đến tham dự seminar của GS. Philippe Quentin.</p>\r\n\r\n<p>Chủ đề: Pairing corrrelations in nuclei, rotational modes and intrinsic vortical currents : the Variable Moment of Inertia model revisited.</p>\r\n\r\n<p>Thời gian: 10h s&aacute;ng, thứ năm 08/12/2016</p>\r\n\r\n<p>Địa điểm: BM Vật l&yacute; hạt nh&acirc;n.</p>\r\n', 'Mời tham dự seminar khoa học của Giáo sư Philippe Quentin'),
(418, 223, 2, 'Invitation to the scientific seminar of Professor Philippe Quentin', '<p>We would like to invite all of you teachers / graduate students / graduate students who are interested in the seminar of Professor. Philippe Quentin.<br />\r\nSubject: Pairing corrrelations in nuclei, rotational modes and intrinsic vortical currents: the Variable Moment of Inertia model revisited.<br />\r\nTime: 10am, Thursday 08/12/2016<br />\r\nLocation: BM Nuclear Physics.</p>\r\n', 'Invitation to the scientific seminar of Professor Philippe Quentin'),
(419, 224, 1, 'Giải thưởng \"Sinh viên nghiên cứu khoa học\" – 2016', '<h3><strong>Giải thưởng &quot;Sinh vi&ecirc;n nghi&ecirc;n cứu khoa học&quot; &ndash; 2016</strong></h3>\r\n\r\n<p>Nhằm tạo s&acirc;n chơi bổ &iacute;ch v&agrave; ph&aacute;t hiện, bồi dưỡng niềm đam m&ecirc; của sinh vi&ecirc;n về khoa học c&ocirc;ng nghệ, qua đ&oacute; c&ocirc;ng nhận c&aacute;c c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu c&oacute; gi&aacute; trị khoa học v&agrave; thực tiễn của sinh vi&ecirc;n trong qu&aacute; tr&igrave;nh học tập tại Trường; đồng thời, giới thiệu c&aacute;c đề t&agrave;i xuất sắc tham dự c&aacute;c hội thi, giải thưởng cấp Th&agrave;nh phố v&agrave; cấp Quốc gia,&nbsp;<strong>Đo&agrave;n Thanh Ni&ecirc;n Trường ĐH Khoa học Tự nhi&ecirc;n, ĐHQG-HCM&nbsp;</strong>tổ chức&nbsp;Giải thưởng&nbsp;<strong>&quot;Sinh vi&ecirc;n nghi&ecirc;n cứu khoa học&quot; năm 2016.</strong></p>\r\n\r\n<p>Để đăng k&yacute;, qu&yacute; thầy c&ocirc; v&agrave; c&aacute;c bạn sinh vi&ecirc;n vui l&ograve;ng điền mẫu th&ocirc;ng tin tại link&nbsp;<strong><a href=\"http://tinyurl.com/sinhviennghiencuukhoahoc\" target=\"_blank\">http://tinyurl.com/sinhviennghiencuukhoahoc</a></strong>, sau đ&oacute; gửi trực tiếp đề t&agrave;i ho&agrave;n chỉnh về Văn ph&ograve;ng đo&agrave;n cơ sở 1 theo như thể lệ. Lưu &yacute; hạn ch&oacute;t gửi về l&agrave; ng&agrave;y&nbsp;<strong>15/8/2016.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://drive.google.com/file/d/0B0yL-xLCx0Yfc0s5TklDUTdmSHFXLXVjNE0talBuN0hlRm5B/view?usp=sharing\">Đ&iacute;nh k&egrave;m</a>&nbsp;l&agrave;&nbsp;<strong>Thể lệ chi tiết&nbsp;</strong>của&nbsp;<strong>Giải thưởng Sinh vi&ecirc;n Nghi&ecirc;n cứu khoa học cấp trường</strong>&nbsp;v&agrave;&nbsp;<strong>Giải thưởng&nbsp;Eur&eacute;ka</strong>&nbsp;<strong>của cấp Th&agrave;nh</strong>&nbsp;(c&aacute;c đề t&agrave;i c&oacute; chất lượng tốt do Hội đồng gi&aacute;m khảo thẩm định tại Giải thưởng Sinh vi&ecirc;n Nghi&ecirc;n cứu khoa học sẽ được giới thiệu tham dự Giải thưởng Eur&eacute;ka). Giải Eur&eacute;ka năm nay c&oacute; sự mở rộng quy m&ocirc; to&agrave;n quốc v&agrave; nhiều hoạt động hỗ trợ hơn cho t&aacute;c giải đề t&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo số lương sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh c&aacute;c bộ m&ocirc;n, Khoa ước lượng số lượng tối thiểu (khuyến kh&iacute;ch đăng k&yacute; nhiều hơn) c&aacute;c đề t&agrave;i đăng k&yacute; tham gia như sau:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>VL Hạt nh&acirc;n + Kỹ thuật hạt nh&acirc;n: 3 -4 đề t&agrave;i (Thầy Trần Thiện Thanh phụ tr&aacute;ch)</p>\r\n\r\n<p>VL L&yacute; thuyết: 1-2 đề t&agrave;i (Thầy V&otilde; Quốc Phong phụ tr&aacute;ch)</p>\r\n\r\n<p>VL Địa cấu: 1-2 đề t&agrave;i (Thầy Đặng Ho&agrave;i Trung phụ tr&aacute;ch)</p>\r\n\r\n<p>VL Ứng dụng: 3-4 đề t&agrave;i (Thầy Nguyễn Hữu Kế phụ tr&aacute;ch)</p>\r\n\r\n<p>VL Chất rắn: 1-2 đề t&agrave;i (C&ocirc; Huỳnh Trần Mỹ H&ograve;a phụ tr&aacute;ch)</p>\r\n\r\n<p>VL Điện tử: 3-4 đề t&agrave;i (Thầy Nguyễn Ch&iacute; Nh&acirc;n phụ tr&aacute;ch)</p>\r\n\r\n<p>VL Tin học: 2-3 đề t&agrave;i (Thầy Nguyễn Ch&iacute; Linh phụ tr&aacute;ch)</p>\r\n\r\n<p>Hải dương -KT-TV: 3-4 đề t&agrave;i (C&ocirc; L&ecirc; Trần Duy Ph&uacute;c phụ tr&aacute;ch)</p>\r\n', 'Giải thưởng \"Sinh viên nghiên cứu khoa học\" – 2016'),
(420, 224, 2, '\"Science research student\" award - 2016', '<p>&quot;Science research student&quot; award - 2016<br />\r\nIn order to create a useful playground and discover and foster students&#39; passion for science and technology, thereby recognizing scientific and practical scientific researches of students during their study at School; At the same time, introducing excellent topics to participate in contests and awards at the city and national level, the Youth Union of the University of Natural Sciences, VNU-HCM held the &quot;Student of Science Research&quot; award. &quot;in 2016.</p>\r\n\r\n<p>To register, teachers and students please fill out the information form at http://tinyurl.com/sinhviennghiencuukhoahoc, then send the complete topic directly to the Youth Union Office 1 according to the rules . Please note that the deadline for return is August 15, 2016.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Attached are the detailed Regulations of the School-level Scientific Research Student Award and the Eur&eacute;ka Prize of the Citizenship (good quality subjects appraised by the Jury at the Student Scientific Research Award will be attached. introduced to the Eur&eacute;ka Awards). This year&#39;s Eur&eacute;ka Prize has been expanded nationally and more supportive activities for the topic awarding.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>According to the number of students majoring in various subjects, the Faculty estimates the minimum number (encouraging more registration) of the following topics of enrollment:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nuclear Physics + Nuclear Engineering: 3-4 topics (Mr. Tran Thien Thanh is in charge)</p>\r\n\r\n<p>VL Theory: 1-2 topics (Mr. Vo Quoc Phong is in charge)</p>\r\n\r\n<p>VL Geography: 1-2 topics (Mr. Dang Hoai Trung is in charge)</p>\r\n\r\n<p>Applied Physics: 3-4 topics (Mr. Nguyen Huu Ke is in charge)</p>\r\n\r\n<p>VL Solids: 1-2 topics (Ms. Huynh Tran My Hoa is in charge)</p>\r\n\r\n<p>Electronic Engineering: 3-4 subjects (Mr. Nguyen Chi Nhan is in charge)</p>\r\n\r\n<p>Computer Science: 2-3 topics (Mr. Nguyen Chi Linh is in charge)</p>\r\n\r\n<p>Hai Duong -KT-TV: 3-4 topics (Ms. Le Tran Duy Phuc is in charge)</p>\r\n', '\"Science research student\" award - 2016'),
(421, 225, 1, 'Mời tham dự Seminar khoa học của Tiến sĩ Phan Thị Minh Điệp', '<p>K&iacute;nh gửi c&aacute;c thầy c&ocirc; th&ocirc;ng tin Seminar khoa học của khoa L&yacute; trong tuần n&agrave;y:</p>\r\n\r\n<p>Tr&igrave;nh b&agrave;y: &nbsp;Tiến sĩ. Phan Thị Minh Điệp, thuộc bộ m&ocirc;n Vật l&yacute; Ứng Dụng (vừa tốt nghiệp tại Đức).&nbsp;</p>\r\n\r\n<p>T&ecirc;n seminar: Đặc điểm, th&agrave;nh phần h&oacute;a học v&agrave; đặc trưng quang phổ của Sapphire</p>\r\n\r\n<p>Thời gian: 14h, thứ tư 25/11/2015</p>\r\n\r\n<p>Địa điểm: Ph&ograve;ng Bộ M&ocirc;n Vật L&yacute; Ứng Dụng (đối diện ph&ograve;ng tổ chức h&agrave;nh ch&iacute;nh)</p>\r\n\r\n<p>K&iacute;nh mời c&aacute;c thầy c&ocirc; quan t&acirc;m đến tham dự.&nbsp;</p>\r\n\r\n<p>Tr&acirc;n trọng,</p>\r\n', 'Mời tham dự Seminar khoa học của Tiến sĩ Phan Thị Minh Điệp'),
(422, 225, 2, 'Invitation to the Scientific Seminar of Dr. Phan Thi Minh Diep', '<p>Dear teachers of the Science Seminar of the Department of Science this week:<br />\r\nPresentation: Doctor. Phan Thi Minh Diep, Department of Applied Physics (just graduated in Germany).<br />\r\nSeminar name: Characteristics, chemical composition and spectral characteristics of Sapphire<br />\r\nTime: 14h, Wednesday 25/11/2015<br />\r\nVenue: Department of Applied Physics (opposite to administrative organization room)<br />\r\nInviting interested teachers to attend.<br />\r\nBest regards,</p>\r\n', 'Invitation to the Scientific Seminar of Dr. Phan Thi Minh Diep'),
(423, 226, 1, 'Thông tin về lớp học quốc tế chuyên đề các hệ thống thời gian thực-The International Real Time School 2016', '<p>Việc thực h&agrave;nh th&iacute; nghiệm rất quan trọng đối với c&aacute;c sinh vi&ecirc;n. Do đ&oacute;, từ ng&agrave;y 18 đến ng&agrave;y 26 th&aacute;ng 7, ph&ograve;ng th&iacute; nghiệm kỹ thuật hạt nh&acirc;n-Trường Đại Học Khoa Học Tự Nhi&ecirc;n TPHCM kết hợp với Hội Nghi&ecirc;n Cứu Vật L&yacute; Hạt Nh&acirc;n v&agrave; Plasma IEEE (IEEE NPSS), Trung T&acirc;m Nghi&ecirc;n Cứu Vật L&yacute; Hạt Nh&acirc;n đại học Osaka (RCNP)&nbsp; tổ chức Lớp Học Quốc Tế Về C&aacute;c Hệ Thống Thời Gian Thực tại Ph&ograve;ng Th&iacute; Nghiệm Kỹ Thuật Hạt Nh&acirc;n cơ sở Linh Trung. Lớp học kh&ocirc;ng chỉ mang đến cho sinh vi&ecirc;n v&agrave; học vi&ecirc;n c&aacute;c b&agrave;i giảng m&agrave; c&ograve;n cung cấp cả những b&agrave;i thực h&agrave;nh tại ph&ograve;ng th&iacute; nghiệm. C&aacute;c b&agrave;i giảng v&agrave; b&agrave;i thực h&agrave;nh do c&aacute;c chuy&ecirc;n gia trong lĩnh vực ghi nhận bức xạ đảm tr&aacute;ch, cung cấp cho người học những kiến thức cập nhật mới nhất. Th&ocirc;ng tin chi tiết hơn về lớp học xin xem tại web site của Ph&ograve;ng Th&iacute; Nghiệm Kỹ Thuật Hạt Nh&acirc;n:</p>\r\n\r\n<p><a href=\"http://ntlab.hcmus.edu.vn/en/rt2016-school/\">http://ntlab.hcmus.edu.vn/en/rt2016-school/</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><embed height=\"400\" pluginspage=\"http://www.macromedia.com/go/getflashplayer\" src=\"https://photos.gstatic.com/media/slideshow.swf\" type=\"application/x-shockwave-flash\" width=\"600\"></embed></p>\r\n\r\n<p><strong>Mục ti&ecirc;u của lớp học l&agrave;:</strong></p>\r\n\r\n<p>-Đ&agrave;o tạo c&aacute;c nh&agrave; khoa học trẻ trong lĩnh vực ghi nhận bức xạ v&agrave; ứng dụng của n&oacute;.</p>\r\n\r\n<p>-K&iacute;ch th&iacute;ch sự ph&aacute;t triển của hệ thời gian thực ở khu vực Đ&ocirc;ng Nam &Aacute;.</p>\r\n\r\n<p>-Th&uacute;c đẩy c&aacute;c nh&agrave; khoa học trẻ tham gia v&agrave;o lĩnh vực đo lường bức xạ cũng như những lĩnh vực li&ecirc;n quan.</p>\r\n\r\n<p><strong>Đối tượng tham gia:</strong></p>\r\n\r\n<p>Lớp học d&agrave;nh cho c&aacute;c bạn sinh vi&ecirc;n v&agrave; học vi&ecirc;n cao học.</p>\r\n\r\n<p>Th&ocirc;ng tin về chương tr&igrave;nh học v&agrave; danh s&aacute;ch lớp học&nbsp;<a href=\"https://drive.google.com/file/d/0B0yL-xLCx0Yfb1h3S0poVGcxYjdlOGk4cEhBcXdxVzhpdXFJ/view?usp=sharing\">tại đ&acirc;y</a>.</p>\r\n\r\n<p><strong>Th&agrave;nh vi&ecirc;n Ban tổ chức lớp học:</strong></p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:653px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Num.</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Full name</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Institution</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Nationality</strong></p>\r\n			</td>\r\n			<td>\r\n			<p><strong>Note</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>1</p>\r\n			</td>\r\n			<td>\r\n			<p>Prof. Chau Van Tao</p>\r\n			</td>\r\n			<td>\r\n			<p>Vice president,</p>\r\n\r\n			<p>University of Science-VNU-HCMC</p>\r\n			</td>\r\n			<td>\r\n			<p>Vietnam</p>\r\n			</td>\r\n			<td>\r\n			<p>General chair (Host)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>2</p>\r\n			</td>\r\n			<td>\r\n			<p>Dr. Truong Thi Hong Loan</p>\r\n			</td>\r\n			<td>\r\n			<p>Head of Nuclear Technique Lab.</p>\r\n\r\n			<p>University of Science-VNU-HCMC</p>\r\n			</td>\r\n			<td>\r\n			<p>Vietnam</p>\r\n			</td>\r\n			<td>\r\n			<p>School chair</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>3</p>\r\n			</td>\r\n			<td>\r\n			<p>Prof. Nguyen Van Hieu</p>\r\n			</td>\r\n			<td>\r\n			<p>Head of international affair Office.</p>\r\n\r\n			<p>University of Science-VNU-HCMC</p>\r\n			</td>\r\n			<td>\r\n			<p>Vietnam</p>\r\n			</td>\r\n			<td>\r\n			<p>International affair</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>4</p>\r\n			</td>\r\n			<td>\r\n	